import { useState, useEffect, createContext, useContext, ReactNode } from 'react';
import { User, Session } from '@supabase/supabase-js';
import { supabase } from '@/integrations/supabase/client';

type AppRole = 'super_admin' | 'restaurant_owner' | 'delivery_partner' | 'customer';

interface AuthContextType {
  user: User | null;
  session: Session | null;
  loading: boolean;
  roles: AppRole[];
  signIn: (email: string, password: string) => Promise<{ error: Error | null }>;
  signUp: (email: string, password: string, fullName: string) => Promise<{ error: Error | null }>;
  signOut: () => Promise<void>;
  hasRole: (role: AppRole) => boolean;
}

const AuthContext = createContext<AuthContextType | undefined>(undefined);

export function AuthProvider({ children }: { children: ReactNode }) {
  const [user, setUser] = useState<User | null>(null);
  const [session, setSession] = useState<Session | null>(null);
  const [loading, setLoading] = useState(true);
  const [roles, setRoles] = useState<AppRole[]>([]);

  const fetchUserRoles = async (userId: string) => {
    const { data, error } = await supabase
      .from('user_roles')
      .select('role')
      .eq('user_id', userId);

    if (!error && data) {
      setRoles(data.map((r) => r.role as AppRole));
    }
  };

  useEffect(() => {
    const { data: { subscription } } = supabase.auth.onAuthStateChange(
      (event, session) => {
        setSession(session);
        setUser(session?.user ?? null);

        if (session?.user) {
          setTimeout(() => {
            fetchUserRoles(session.user.id);
          }, 0);
        } else {
          setRoles([]);
        }
        setLoading(false);
      }
    );

    supabase.auth.getSession().then(({ data: { session } }) => {
      setSession(session);
      setUser(session?.user ?? null);
      if (session?.user) {
        fetchUserRoles(session.user.id);
      }
      setLoading(false);
    });

    return () => subscription.unsubscribe();
  }, []);

  const signIn = async (email: string, password: string) => {
    const { error } = await supabase.auth.signInWithPassword({ email, password });
    return { error };
  };

  const signUp = async (email: string, password: string, fullName: string) => {
    const redirectUrl = `${window.location.origin}/`;
    const { error } = await supabase.auth.signUp({
      email,
      password,
      options: {
        emailRedirectTo: redirectUrl,
        data: { full_name: fullName },
      },
    });
    return { error };
  };

  const signOut = async () => {
    await supabase.auth.signOut();
    setRoles([]);
  };

  const hasRole = (role: AppRole) => roles.includes(role);

  return (
    <AuthContext.Provider value={{ user, session, loading, roles, signIn, signUp, signOut, hasRole }}>
      {children}
    </AuthContext.Provider>
  );
}

export function useAuth() {
  const context = useContext(AuthContext);
  if (context === undefined) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
}

import { useState, useEffect, useCallback } from 'react';
import { Link, useParams, useNavigate } from 'react-router-dom';
import { supabase } from '@/integrations/supabase/client';
import { useAuth } from '@/hooks/useAuth';
import { Order, OrderStatus, DeliveryPartner } from '@/types/database';
import { Button } from '@/components/ui/button';
import { Card, CardContent } from '@/components/ui/card';
import { StatusBadge } from '@/components/StatusBadge';
import { ReviewForm } from '@/components/ReviewForm';
import {
  ArrowLeft,
  Package,
  Clock,
  MapPin,
  Phone,
  Key,
  Navigation,
  Bike,
  Store,
  Home,
  CheckCircle2
} from 'lucide-react';
import { format } from 'date-fns';
import { toast } from 'sonner';
import { useCustomerOrderNotifications } from '@/hooks/useOrderNotifications';

interface OrderWithDetails extends Order {
  restaurants: {
    name: string;
    phone: string | null;
    lat: number | null;
    lng: number | null;
    address: string;
  } | null;
}

interface DeliveryPartnerWithProfile extends DeliveryPartner {
  profiles?: { full_name: string | null; phone: string | null } | null;
}

const orderSteps: { status: OrderStatus; label: string; icon: React.ReactNode }[] = [
  { status: 'pending', label: 'Order Placed', icon: <Package className="w-4 h-4" /> },
  { status: 'confirmed', label: 'Confirmed', icon: <CheckCircle2 className="w-4 h-4" /> },
  { status: 'preparing', label: 'Preparing', icon: <Store className="w-4 h-4" /> },
  { status: 'ready_for_pickup', label: 'Ready', icon: <Package className="w-4 h-4" /> },
  { status: 'picked_up', label: 'Picked Up', icon: <Bike className="w-4 h-4" /> },
  { status: 'on_the_way', label: 'On the Way', icon: <Navigation className="w-4 h-4" /> },
  { status: 'delivered', label: 'Delivered', icon: <Home className="w-4 h-4" /> },
];

const getStepIndex = (status: OrderStatus) => {
  if (status === 'cancelled') return -1;
  return orderSteps.findIndex(s => s.status === status);
};

export default function OrderTracking() {
  const { orderId } = useParams<{ orderId: string }>();
  const { user, loading: authLoading } = useAuth();
  const navigate = useNavigate();
  const [order, setOrder] = useState<OrderWithDetails | null>(null);
  const [deliveryPartner, setDeliveryPartner] = useState<DeliveryPartnerWithProfile | null>(null);
  const [hasReviewed, setHasReviewed] = useState(false);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (!authLoading && !user) {
      navigate('/auth');
      return;
    }
    if (user && orderId) {
      fetchOrder();
      const cleanup = subscribeToUpdates();
      return cleanup;
    }
  }, [user, authLoading, orderId, navigate, fetchOrder, subscribeToUpdates]);

  // Sound notifications for order status changes
  useCustomerOrderNotifications(orderId);

  const fetchOrder = useCallback(async () => {
    const { data, error } = await supabase
      .from('orders')
      .select('*, restaurants(name, phone, lat, lng, address)')
      .eq('id', orderId!)
      .eq('customer_id', user!.id)
      .single();

    if (error || !data) {
      toast.error('Order not found');
      navigate('/orders');
      return;
    }

    setOrder(data);

    // Check if user has already reviewed this order
    const { data: reviewData } = await supabase
      .from('restaurant_reviews')
      .select('id')
      .eq('order_id', orderId!)
      .maybeSingle();

    setHasReviewed(!!reviewData);

    // Fetch delivery partner if assigned
    if (data.delivery_partner_id) {
      const { data: partnerData } = await supabase
        .from('delivery_partners')
        .select('*') \n        .eq('id', data.delivery_partner_id) \n.single(); \n\n      if (partnerData) { \n        setDeliveryPartner(partnerData); \n } \n
    } \n\n    setLoading(false); \n
  }, [orderId, user, navigate]);

  const subscribeToUpdates = useCallback(() => {
    const channel = supabase
      .channel(`order-${orderId}`)
      .on(
        'postgres_changes',
        {
          event: '*',
          schema: 'public',
          table: 'orders',
          filter: `id=eq.${orderId}`,
        },
        () => {
          fetchOrder();
        }
      )
      .on(
        'postgres_changes',
        {
          event: '*',
          schema: 'public',
          table: 'delivery_partners',
        },
        () => {
          if (order?.delivery_partner_id) {
            fetchOrder();
          }
        }
      )
      .subscribe();

    return () => {
      supabase.removeChannel(channel);
    };
  }, [orderId, fetchOrder, order?.delivery_partner_id]);

  if (authLoading || loading) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center">
        <div className="animate-pulse text-muted-foreground">Loading...</div>
      </div>
    );
  }

  if (!order) {
    return null;
  }

  const currentStep = getStepIndex(order.status as OrderStatus);
  const isCancelled = order.status === 'cancelled';
  const isDelivered = order.status === 'delivered';
  const isActive = !isCancelled && !isDelivered;

  return (
    <div className="min-h-screen bg-background">
      <header className="sticky top-0 z-50 bg-card/95 backdrop-blur-md border-b border-border">
        <div className="container mx-auto px-4 py-4">
          <div className="flex items-center gap-4">
            <Link to="/orders" className="flex items-center gap-2 text-foreground hover:text-primary transition-colors">
              <ArrowLeft className="w-5 h-5" />
            </Link>
            <h1 className="text-xl font-bold">Track Order</h1>
          </div>
        </div>
      </header>

      <div className="container mx-auto px-4 py-6 space-y-6">
        {/* Order Header */}
        <Card className="border-0 shadow-sm overflow-hidden">
          <div className="bg-gradient-to-r from-primary/10 to-primary/5 p-4">
            <div className="flex items-start justify-between">
              <div>
                <h2 className="font-bold text-lg">{order.restaurants?.name}</h2>
                <p className="text-sm text-muted-foreground font-mono">
                  #{order.id.slice(0, 8).toUpperCase()}
                </p>
              </div>
              <StatusBadge status={order.status as OrderStatus} />
            </div>
          </div>

          <CardContent className="p-4">
            <div className="flex items-center gap-2 text-sm text-muted-foreground">
              <Clock className="w-4 h-4" />
              <span>Ordered: {format(new Date(order.created_at), 'MMM d, yyyy h:mm a')}</span>
            </div>
            {order.is_scheduled && order.scheduled_at && (
              <div className="flex items-center gap-2 text-sm text-primary mt-2">
                <Clock className="w-4 h-4" />
                <span>Scheduled for: {format(new Date(order.scheduled_at), 'MMM d, h:mm a')}</span>
              </div>
            )}
          </CardContent>
        </Card>

        {/* Order Tracking Progress */}
        {isActive && (
          <Card className="border-0 shadow-sm">
            <CardContent className="p-4">
              <h3 className="font-semibold mb-4">Order Status</h3>
              <div className="relative">
                {/* Progress Line */}
                <div className="absolute left-[19px] top-0 bottom-0 w-0.5 bg-border" />
                <div
                  className="absolute left-[19px] top-0 w-0.5 bg-primary transition-all duration-500"
                  style={{ height: `${(currentStep / (orderSteps.length - 1)) * 100}%` }}
                />

                <div className="space-y-4">
                  {orderSteps.map((step, index) => {
                    const isCompleted = index <= currentStep;
                    const isCurrent = index === currentStep;

                    return (
                      <div key={step.status} className="flex items-center gap-4 relative">
                        <div className={`w-10 h-10 rounded-full flex items-center justify-center z-10 transition-all ${isCompleted
                          ? 'bg-primary text-primary-foreground'
                          : 'bg-muted text-muted-foreground'
                          } ${isCurrent ? 'ring-4 ring-primary/20 animate-pulse-soft' : ''}`}>
                          {step.icon}
                        </div>
                        <div className="flex-1">
                          <p className={`font-medium ${isCompleted ? 'text-foreground' : 'text-muted-foreground'}`}>
                            {step.label}
                          </p>
                          {isCurrent && (
                            <p className="text-sm text-primary animate-fade-in">In progress...</p>
                          )}
                        </div>
                      </div>
                    );
                  })}
                </div>
              </div>
            </CardContent>
          </Card>
        )}

        {/* Delivery Partner Info */}
        {deliveryPartner && isActive && (
          <Card className="border-0 shadow-sm">
            <CardContent className="p-4">
              <h3 className="font-semibold mb-3">Delivery Partner</h3>
              <div className="flex items-center justify-between">
                <div className="flex items-center gap-3">
                  <div className="w-12 h-12 rounded-full bg-primary/10 flex items-center justify-center">
                    <Bike className="w-6 h-6 text-primary" />
                  </div>
                  <div>
                    <p className="font-medium">Delivery Partner</p>
                    <p className="text-sm text-muted-foreground capitalize">
                      {deliveryPartner.vehicle_type || 'Bike'}
                    </p>
                  </div>
                </div>
                {deliveryPartner.phone && (
                  <a href={`tel:${deliveryPartner.phone}`}>
                    <Button size="icon" variant="outline" className="rounded-full">
                      <Phone className="w-4 h-4" />
                    </Button>
                  </a>
                )}
              </div>

              {/* Partner Location */}
              {deliveryPartner.current_lat && deliveryPartner.current_lng && (
                <div className="mt-4 p-3 bg-accent/10 rounded-lg">
                  <div className="flex items-center gap-2 text-accent">
                    <Navigation className="w-4 h-4" />
                    <span className="text-sm font-medium">Partner is nearby</span>
                  </div>
                  <p className="text-xs text-muted-foreground mt-1">
                    Location: {deliveryPartner.current_lat.toFixed(4)}, {deliveryPartner.current_lng.toFixed(4)}
                  </p>
                </div>
              )}
            </CardContent>
          </Card>
        )}

        {/* Delivery OTP */}
        {order.delivery_otp && isActive && (
          <Card className="border-0 shadow-sm border-primary/20 bg-primary/5">
            <CardContent className="p-4">
              <div className="flex items-center gap-3">
                <div className="w-10 h-10 rounded-full bg-primary/10 flex items-center justify-center">
                  <Key className="w-5 h-5 text-primary" />
                </div>
                <div className="flex-1">
                  <p className="text-sm text-muted-foreground">Delivery OTP</p>
                  <p className="font-mono font-bold text-2xl text-primary tracking-[0.5em]">
                    {order.delivery_otp}
                  </p>
                </div>
              </div>
              <p className="text-xs text-muted-foreground mt-3">
                Share this code with your delivery partner to confirm delivery
              </p>
            </CardContent>
          </Card>
        )}

        {/* Addresses */}
        <Card className="border-0 shadow-sm">
          <CardContent className="p-4 space-y-4">
            {/* Shop Address */}
            <div className="flex items-start gap-3">
              <div className="w-8 h-8 rounded-full bg-accent/10 flex items-center justify-center shrink-0">
                <Store className="w-4 h-4 text-accent" />
              </div>
              <div className="flex-1">
                <p className="text-sm text-muted-foreground">Pickup from</p>
                <p className="font-medium">{order.restaurants?.name}</p>
                <p className="text-sm text-muted-foreground">{order.restaurants?.address}</p>
              </div>
              {order.restaurants?.phone && (
                <a href={`tel:${order.restaurants.phone}`}>
                  <Button size="icon" variant="ghost" className="shrink-0">
                    <Phone className="w-4 h-4" />
                  </Button>
                </a>
              )}
            </div>

            <div className="border-l-2 border-dashed border-border ml-4 h-4" />

            {/* Delivery Address */}
            <div className="flex items-start gap-3">
              <div className="w-8 h-8 rounded-full bg-primary/10 flex items-center justify-center shrink-0">
                <MapPin className="w-4 h-4 text-primary" />
              </div>
              <div className="flex-1">
                <p className="text-sm text-muted-foreground">Deliver to</p>
                <p className="font-medium">{order.delivery_address}</p>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Order Total */}
        <Card className="border-0 shadow-sm">
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <span className="text-muted-foreground">Total Amount</span>
              <span className="font-bold text-xl text-primary">
                {Number(order.total_amount).toFixed(2)}
              </span>
            </div>
          </CardContent>
        </Card>

        {/* Cancelled State */}
        {isCancelled && (
          <Card className="border-0 shadow-sm border-destructive/20 bg-destructive/5">
            <CardContent className="p-4 text-center">
              <p className="text-destructive font-medium">This order has been cancelled</p>
            </CardContent>
          </Card>
        )}

        {/* Delivered State */}
        {isDelivered && (
          <>
            <Card className="border-0 shadow-sm border-accent/20 bg-accent/5">
              <CardContent className="p-4 text-center">
                <div className="w-12 h-12 rounded-full bg-accent/10 flex items-center justify-center mx-auto mb-2">
                  <CheckCircle2 className="w-6 h-6 text-accent" />
                </div>
                <p className="text-accent font-medium">Order Delivered Successfully!</p>
              </CardContent>
            </Card>

            {/* Review Form - Show only if not reviewed */}
            {!hasReviewed && order.restaurants && (
              <ReviewForm
                orderId={order.id}
                restaurantId={order.restaurant_id}
                customerId={user!.id}
                restaurantName={order.restaurants.name}
                onReviewSubmitted={() => setHasReviewed(true)}
              />
            )}

            {/* Already Reviewed Message */}
            {hasReviewed && (
              <Card className="border-0 shadow-sm">
                <CardContent className="p-4 text-center">
                  <p className="text-muted-foreground">Thank you for your review!</p>
                </CardContent>
              </Card>
            )}
          </>
        )}
      </div>
    </div>
  );
}

import React, { useState, useEffect, forwardRef } from 'react';
import { useParams, Link } from 'react-router-dom';
import { supabase } from '@/integrations/supabase/client';
import { Restaurant, MenuItem } from '@/types/database';
import { useCartStore } from '@/stores/cartStore';
import { useAuth } from '@/hooks/useAuth';
import { Button } from '@/components/ui/button';
import { Card, CardContent } from '@/components/ui/card';
import { RestaurantReviews } from '@/components/RestaurantReviews';

import {
  ArrowLeft,
  MapPin,
  Clock,
  Star,
  Plus,
  Minus,
  ShoppingCart,
  Utensils
} from 'lucide-react';
import { toast } from 'sonner';
import { useHaptics } from "@/hooks/useHaptics";
import { ImpactStyle, NotificationType } from "@capacitor/haptics";

const RestaurantDetail = forwardRef<HTMLDivElement>((_, ref) => {
  const { id } = useParams<{ id: string }>();
  const { user } = useAuth();
  const [restaurant, setRestaurant] = useState<Restaurant | null>(null);
  const [menuItems, setMenuItems] = useState<MenuItem[]>([]);
  const [reviewStats, setReviewStats] = useState({ average: 0, count: 0 });
  const [loading, setLoading] = useState(true);
  const { items, addItem, updateQuantity, getTotalItems, getTotalAmount } = useCartStore();
  const { impact, notification } = useHaptics();

  useEffect(() => {
    if (id) {
      fetchRestaurantAndMenu();
    }
  }, [id]);

  const fetchRestaurantAndMenu = async () => {
    const [restaurantRes, menuRes, reviewsRes] = await Promise.all([
      supabase.from('restaurants').select('*').eq('id', id).maybeSingle(),
      supabase.from('menu_items').select('*').eq('restaurant_id', id).eq('is_available', true).order('category'),
      supabase.from('restaurant_reviews').select('rating').eq('restaurant_id', id),
    ]);

    if (restaurantRes.data) {
      setRestaurant(restaurantRes.data);
    }
    if (menuRes.data) {
      setMenuItems(menuRes.data);
    }
    if (reviewsRes.data && reviewsRes.data.length > 0) {
      const avg = reviewsRes.data.reduce((sum, r) => sum + r.rating, 0) / reviewsRes.data.length;
      setReviewStats({ average: avg, count: reviewsRes.data.length });
    }
    setLoading(false);
  };

  const getItemQuantity = (menuItemId: string) => {
    const cartItem = items.find((item) => item.menuItem.id === menuItemId);
    return cartItem?.quantity || 0;
  };

  const handleAddItem = (menuItem: MenuItem) => {
    if (!user) {
      toast.error('Please sign in to add items to cart');
      notification(NotificationType.Error);
      return;
    }
    if (!restaurant?.is_open) {
      toast.error('This shop is currently closed');
      notification(NotificationType.Error);
      return;
    }
    impact(ImpactStyle.Medium);
    addItem(menuItem, restaurant!.id);
    toast.success(`Added ${menuItem.name} to cart`);
  };

  const handleUpdateQuantity = (menuItemId: string, delta: number) => {
    impact(ImpactStyle.Light);
    const currentQty = getItemQuantity(menuItemId);
    updateQuantity(menuItemId, currentQty + delta);
  };

  const groupedItems = menuItems.reduce((acc, item) => {
    const category = item.category || 'Other';
    if (!acc[category]) acc[category] = [];
    acc[category].push(item);
    return acc;
  }, {} as Record<string, MenuItem[]>);

  if (loading) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center">
        <div className="animate-pulse text-muted-foreground">Loading...</div>
      </div>
    );
  }

  if (!restaurant) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center">
        <div className="text-center">
          <h2 className="text-xl font-semibold mb-2">Shop not found</h2>
          <Link to="/">
            <Button variant="outline">Back to Home</Button>
          </Link>
        </div>
      </div>
    );
  }

  return (
    <div ref={ref} className="min-h-screen bg-background">
      {/* Header */}
      <header className="sticky top-0 z-50 bg-card/95 backdrop-blur-md border-b border-border">
        <div className="container mx-auto px-4 py-4">
          <div className="flex items-center justify-between">
            <Link to="/" className="flex items-center gap-2 text-foreground hover:text-primary transition-colors">
              <ArrowLeft className="w-5 h-5" />
              <span>Back</span>
            </Link>
            <Link to="/cart" className="relative">
              <Button variant="outline" size="icon">
                <ShoppingCart className="w-5 h-5" />
                {getTotalItems() > 0 && (
                  <span className="absolute -top-1 -right-1 w-5 h-5 bg-primary text-primary-foreground text-xs rounded-full flex items-center justify-center">
                    {getTotalItems()}
                  </span>
                )}
              </Button>
            </Link>
          </div>
        </div>
      </header>

      {/* Restaurant Hero */}
      <div className="relative h-64 lg:h-80 bg-gradient-to-br from-secondary to-muted overflow-hidden">
        {restaurant.image_url ? (
          <img
            src={restaurant.image_url}
            alt={restaurant.name}
            className="w-full h-full object-cover"
          />
        ) : (
          <div className="w-full h-full flex items-center justify-center">
            <Utensils className="w-24 h-24 text-muted-foreground/30" />
          </div>
        )}
        <div className="absolute inset-0 bg-gradient-to-t from-background/90 to-transparent" />
        <div className="absolute bottom-0 left-0 right-0 p-6">
          <div className="container mx-auto">
            <div className={`inline-flex px-3 py-1 rounded-full text-sm font-medium mb-3 ${restaurant.is_open
              ? 'bg-accent text-accent-foreground'
              : 'bg-destructive text-destructive-foreground'
              }`}>
              {restaurant.is_open ? 'Open Now' : 'Closed'}
            </div>
            <h1 className="text-3xl lg:text-4xl font-bold text-foreground mb-2">{restaurant.name}</h1>
            {restaurant.cuisine_type && (
              <p className="text-muted-foreground mb-3">{restaurant.cuisine_type}</p>
            )}
            <div className="flex flex-wrap items-center gap-4 text-sm text-muted-foreground">
              <div className="flex items-center gap-1">
                <Star className="w-4 h-4 fill-status-pending text-status-pending" />
                <span className="font-medium text-foreground">
                  {reviewStats.count > 0 ? reviewStats.average.toFixed(1) : '4.5'}
                </span>
                <span>({reviewStats.count > 0 ? `${reviewStats.count} reviews` : '120+ ratings'})</span>
              </div>
              <div className="flex items-center gap-1">
                <Clock className="w-4 h-4" />
                <span>25-35 min</span>
              </div>
              <div className="flex items-center gap-1">
                <MapPin className="w-4 h-4" />
                <span>{restaurant.address}</span>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Menu */}
      <div className="container mx-auto px-4 py-8">
        {Object.keys(groupedItems).length === 0 ? (
          <div className="text-center py-16">
            <Utensils className="w-16 h-16 text-muted-foreground/30 mx-auto mb-4" />
            <h3 className="text-xl font-semibold mb-2">No menu items yet</h3>
            <p className="text-muted-foreground">This shop hasn't added any items to their menu.</p>
          </div>
        ) : (
          <div className="space-y-10">
            {Object.entries(groupedItems).map(([category, items]) => (
              <div key={category}>
                <h2 className="text-xl font-bold text-foreground mb-4 pb-2 border-b border-border">
                  {category}
                </h2>
                <div className="grid gap-4">
                  {items.map((item) => {
                    const quantity = getItemQuantity(item.id);
                    return (
                      <Card key={item.id} className="overflow-hidden border-0 shadow-sm hover:shadow-md transition-shadow">
                        <CardContent className="p-0">
                          <div className="flex">
                            <div className="flex-1 p-4">
                              <h3 className="font-semibold text-foreground mb-1">{item.name}</h3>
                              {item.description && (
                                <p className="text-sm text-muted-foreground mb-2 line-clamp-2">
                                  {item.description}
                                </p>
                              )}
                              <p className="text-lg font-bold text-primary">{Number(item.price).toFixed(2)}</p>
                            </div>
                            <div className="flex flex-col items-center justify-center p-4 gap-2">
                              {item.image_url && (
                                <img
                                  src={item.image_url}
                                  alt={item.name}
                                  className="w-20 h-20 object-cover rounded-lg"
                                />
                              )}
                              {quantity > 0 ? (
                                <div className="flex items-center gap-2">
                                  <Button
                                    size="icon"
                                    variant="outline"
                                    className="h-8 w-8"
                                    onClick={() => handleUpdateQuantity(item.id, -1)}
                                  >
                                    <Minus className="w-4 h-4" />
                                  </Button>
                                  <span className="w-8 text-center font-medium">{quantity}</span>
                                  <Button
                                    size="icon"
                                    variant="outline"
                                    className="h-8 w-8"
                                    onClick={() => handleUpdateQuantity(item.id, 1)}
                                  >
                                    <Plus className="w-4 h-4" />
                                  </Button>
                                </div>
                              ) : (
                                <Button
                                  size="sm"
                                  onClick={() => handleAddItem(item)}
                                  disabled={!restaurant.is_open}
                                >
                                  <Plus className="w-4 h-4 mr-1" />
                                  Add
                                </Button>
                              )}
                            </div>
                          </div>
                        </CardContent>
                      </Card>
                    );
                  })}
                </div>
              </div>
            ))}
          </div>
        )}

        {/* Customer Reviews Section */}
        <div className="mt-10">
          <RestaurantReviews restaurantId={id!} />
        </div>
      </div>

      {/* Floating Cart Button */}
      {getTotalItems() > 0 && (
        <div className="fixed bottom-6 left-4 right-4 z-50">
          <div className="container mx-auto max-w-lg">
            <Link to="/cart">
              <Button className="w-full h-14 shadow-lg" size="lg">
                <ShoppingCart className="w-5 h-5 mr-2" />
                <span>View Cart ({getTotalItems()})</span>
                <span className="ml-auto">{getTotalAmount().toFixed(2)}</span>
              </Button>
            </Link>
          </div>
        </div>
      )}
    </div>
  );
});

RestaurantDetail.displayName = 'RestaurantDetail';

export default RestaurantDetail;

import { useState, useEffect, useCallback } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { supabase } from '@/integrations/supabase/client';
import { useAuth } from '@/hooks/useAuth';
import { Restaurant, Order, OrderStatus, DeliveryPartner, AppRole } from '@/types/database';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Input } from '@/components/ui/input';
import { Switch } from '@/components/ui/switch';
import { StatusBadge } from '@/components/StatusBadge';
import { ScrollArea } from '@/components/ui/scroll-area';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
  AlertDialogTrigger,
} from '@/components/ui/alert-dialog';
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from '@/components/ui/select';
import {
  Shield,
  Store,
  Package,
  DollarSign,
  ArrowLeft,
  Check,
  X,
  Percent,
  Bike,
  MapPin,
  TrendingUp,
  Clock,
  Users,
  UserPlus,
  Sparkles,
  Tag,
  RefreshCw,
  Search,
  Phone,
  IndianRupee,
  Filter
} from 'lucide-react';
import { PromotionsManager } from '@/components/admin/PromotionsManager';
import { CouponsManager } from '@/components/admin/CouponsManager';
import { toast } from 'sonner';
import { format } from 'date-fns';

interface RestaurantWithStats extends Restaurant {
  orders_count?: number;
}

interface OrderWithDetails extends Order {
  restaurants: { name: string } | null;
}

interface DeliveryPartnerWithProfile extends DeliveryPartner {
  user_id: string;
}

interface UserWithRoles {
  id: string;
  email: string;
  full_name: string | null;
  roles: AppRole[];
}

export default function AdminDashboard() {
  const { user, hasRole, loading: authLoading } = useAuth();
  const navigate = useNavigate();
  const [restaurants, setRestaurants] = useState<RestaurantWithStats[]>([]);
  const [orders, setOrders] = useState<OrderWithDetails[]>([]);
  const [deliveryPartners, setDeliveryPartners] = useState<DeliveryPartnerWithProfile[]>([]);
  const [users, setUsers] = useState<UserWithRoles[]>([]);
  const [stats, setStats] = useState({
    totalRestaurants: 0,
    verifiedRestaurants: 0,
    totalOrders: 0,
    totalRevenue: 0,
    activeOrders: 0,
    totalPartners: 0,
    totalUsers: 0,
    deliveryFeeEarnings: 0,
    platformFeeEarnings: 0,
  });
  const [loading, setLoading] = useState(true);
  const [refreshing, setRefreshing] = useState(false);
  const [commissionUpdates, setCommissionUpdates] = useState<Record<string, string>>({});
  const [selectedRole, setSelectedRole] = useState<Record<string, AppRole>>({});
  const [searchShops, setSearchShops] = useState('');
  const [searchOrders, setSearchOrders] = useState('');
  const [searchUsers, setSearchUsers] = useState('');
  const [orderStatusFilter, setOrderStatusFilter] = useState<string>('all');

  const fetchData = useCallback(async () => {
    const [restaurantsRes, ordersRes, partnersRes, profilesRes] = await Promise.all([
      supabase.from('restaurants').select('*').order('created_at', { ascending: false }),
      supabase
        .from('orders')
        .select('*, restaurants(name)')
        .order('created_at', { ascending: false })
        .limit(100),
      supabase.from('delivery_partners').select('*'),
      supabase.from('profiles').select('id, email, full_name').order('created_at', { ascending: false }).limit(100),
    ]);

    if (restaurantsRes.data) {
      setRestaurants(restaurantsRes.data);
      setStats((prev) => ({
        ...prev,
        totalRestaurants: restaurantsRes.data.length,
        verifiedRestaurants: restaurantsRes.data.filter((r) => r.is_verified).length,
      }));
    }

    if (ordersRes.data) {
      setOrders(ordersRes.data);
      const activeOrders = ordersRes.data.filter(o =>
        !['delivered', 'cancelled'].includes(o.status)
      ).length;
      const totalRevenue = ordersRes.data.reduce(
        (sum, order) => sum + Number(order.total_amount),
        0
      );
      // Calculate delivery fee and platform fee earnings (from delivered orders only)
      const deliveredOrders = ordersRes.data.filter(o => o.status === 'delivered');
      const deliveryFeeEarnings = deliveredOrders.reduce(
        (sum, order) => sum + Number(order.delivery_fee || 25),
        0
      );
      const platformFee = 8; // Fixed platform fee per order
      const platformFeeEarnings = deliveredOrders.length * platformFee;
      setStats((prev) => ({
        ...prev,
        totalOrders: ordersRes.data.length,
        totalRevenue,
        activeOrders,
        deliveryFeeEarnings,
        platformFeeEarnings,
      }));
    }

    if (partnersRes.data) {
      setDeliveryPartners(partnersRes.data);
      setStats((prev) => ({
        ...prev,
        totalPartners: partnersRes.data.length,
      }));
    }

    // Fetch users with their roles
    if (profilesRes.data) {
      const userIds = profilesRes.data.map(p => p.id);
      const { data: rolesData } = await supabase
        .from('user_roles')
        .select('user_id, role')
        .in('user_id', userIds);

      const usersWithRoles: UserWithRoles[] = profilesRes.data.map(profile => ({
        ...profile,
        roles: rolesData?.filter(r => r.user_id === profile.id).map(r => r.role as AppRole) || []
      }));

      setUsers(usersWithRoles);
      setStats((prev) => ({
        ...prev,
        totalUsers: profilesRes.data.length,
      }));
    }

    setLoading(false);
    setRefreshing(false);
  }, []);

  useEffect(() => {
    if (!authLoading) {
      if (!user || !hasRole('super_admin')) {
        toast.error('Access denied');
        navigate('/');
        return;
      }
      fetchData();
      subscribeToChanges();
    }
  }, [user, authLoading, hasRole, fetchData]);

  const handleRefresh = () => {
    setRefreshing(true);
    fetchData();
  };

  // Filtered data
  const filteredRestaurants = restaurants.filter(r =>
    r.name.toLowerCase().includes(searchShops.toLowerCase()) ||
    (r.cuisine_type?.toLowerCase().includes(searchShops.toLowerCase()))
  );

  const filteredOrders = orders.filter(o => {
    const matchesSearch = o.id.toLowerCase().includes(searchOrders.toLowerCase()) ||
      (o.restaurants?.name?.toLowerCase().includes(searchOrders.toLowerCase()));
    const matchesStatus = orderStatusFilter === 'all' || o.status === orderStatusFilter;
    return matchesSearch && matchesStatus;
  });

  const filteredUsers = users.filter(u =>
    (u.full_name?.toLowerCase().includes(searchUsers.toLowerCase())) ||
    u.email.toLowerCase().includes(searchUsers.toLowerCase())
  );

  const subscribeToChanges = () => {
    const channel = supabase
      .channel('admin-changes')
      .on('postgres_changes', { event: '*', schema: 'public', table: 'orders' }, fetchData)
      .on('postgres_changes', { event: '*', schema: 'public', table: 'restaurants' }, fetchData)
      .on('postgres_changes', { event: '*', schema: 'public', table: 'delivery_partners' }, fetchData)
      .subscribe();

    return () => {
      supabase.removeChannel(channel);
    };
  };

  const toggleVerification = async (restaurantId: string, isVerified: boolean) => {
    const { error } = await supabase
      .from('restaurants')
      .update({ is_verified: isVerified })
      .eq('id', restaurantId);

    if (error) {
      toast.error('Failed to update verification status');
    } else {
      toast.success(isVerified ? 'Shop verified' : 'Verification removed');
      fetchData();
    }
  };

  const updateCommission = async (restaurantId: string) => {
    const rate = parseFloat(commissionUpdates[restaurantId]);
    if (isNaN(rate) || rate < 0 || rate > 100) {
      toast.error('Invalid commission rate');
      return;
    }

    const { error } = await supabase
      .from('restaurants')
      .update({ commission_rate: rate })
      .eq('id', restaurantId);

    if (error) {
      toast.error('Failed to update commission rate');
    } else {
      toast.success('Commission rate updated');
      setCommissionUpdates((prev) => ({ ...prev, [restaurantId]: '' }));
      fetchData();
    }
  };

  const cancelOrder = async (orderId: string) => {
    const { error } = await supabase
      .from('orders')
      .update({ status: 'cancelled' })
      .eq('id', orderId);

    if (error) {
      toast.error('Failed to cancel order');
    } else {
      toast.success('Order cancelled');
      fetchData();
    }
  };

  const addRoleToUser = async (userId: string) => {
    const role = selectedRole[userId];
    if (!role) {
      toast.error('Please select a role');
      return;
    }

    // Check if user already has this role
    const userRoles = users.find(u => u.id === userId)?.roles || [];
    if (userRoles.includes(role)) {
      toast.error('User already has this role');
      return;
    }

    // Use secure admin RPC function
    const { data, error } = await supabase.rpc('admin_assign_role', {
      _target_user_id: userId,
      _role: role
    });

    if (error || !data) {
      toast.error('Failed to add role');
    } else {
      toast.success(`Role ${role} added successfully`);
      setSelectedRole(prev => ({ ...prev, [userId]: '' as AppRole }));
      fetchData();
    }
  };

  const removeRoleFromUser = async (userId: string, role: AppRole) => {
    // Use secure admin RPC function
    const { data, error } = await supabase.rpc('admin_remove_role', {
      _target_user_id: userId,
      _role: role
    });

    if (error || !data) {
      toast.error('Failed to remove role');
    } else {
      toast.success(`Role ${role} removed`);
      fetchData();
    }
  };

  if (authLoading || loading) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center">
        <div className="animate-pulse text-muted-foreground">Loading...</div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-background via-secondary/20 to-background">
      {/* Header */}
      <header className="sticky top-0 z-50 bg-card/95 backdrop-blur-md border-b border-border shadow-sm">
        <div className="container mx-auto px-4 py-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-4">
              <Link to="/" className="flex items-center gap-2 text-foreground hover:text-primary transition-colors">
                <ArrowLeft className="w-5 h-5" />
              </Link>
              <div className="flex items-center gap-3">
                <div className="w-10 h-10 rounded-xl bg-gradient-to-br from-primary to-primary/80 hidden md:flex items-center justify-center shadow-lg">
                  <Shield className="w-5 h-5 text-primary-foreground" />
                </div>
                <div>
                  <h1 className="text-xl font-bold bg-gradient-to-r from-foreground to-muted-foreground bg-clip-text">
                    Admin Dashboard
                  </h1>
                  <p className="text-xs text-muted-foreground">Manage your platform</p>
                </div>
              </div>
            </div>
            <Button
              variant="ghost"
              size="icon"
              onClick={handleRefresh}
              disabled={refreshing}
              className="h-9 w-9"
            >
              <RefreshCw className={`w-4 h-4 ${refreshing ? 'animate-spin' : ''}`} />
            </Button>
          </div>
        </div>
      </header>

      <div className="container mx-auto px-4 py-6 space-y-6">
        {/* Stats Grid */}
        <div className="grid grid-cols-2 lg:grid-cols-7 gap-3">
          <Card className="border-0 shadow-md bg-gradient-to-br from-primary/5 to-primary/10">
            <CardContent className="p-4">
              <div className="flex items-center gap-3">
                <div className="w-10 h-10 rounded-xl bg-primary/20 flex items-center justify-center">
                  <Store className="w-5 h-5 text-primary" />
                </div>
                <div>
                  <p className="text-xs text-muted-foreground font-medium">Shops</p>
                  <p className="text-2xl font-bold text-primary">{stats.totalRestaurants}</p>
                </div>
              </div>
            </CardContent>
          </Card>

          <Card className="border-0 shadow-md bg-gradient-to-br from-accent/5 to-accent/10">
            <CardContent className="p-4">
              <div className="flex items-center gap-3">
                <div className="w-10 h-10 rounded-xl bg-accent/20 flex items-center justify-center">
                  <Check className="w-5 h-5 text-accent" />
                </div>
                <div>
                  <p className="text-xs text-muted-foreground font-medium">Verified</p>
                  <p className="text-2xl font-bold text-accent">{stats.verifiedRestaurants}</p>
                </div>
              </div>
            </CardContent>
          </Card>

          <Card className="border-0 shadow-md bg-gradient-to-br from-status-confirmed/5 to-status-confirmed/10">
            <CardContent className="p-4">
              <div className="flex items-center gap-3">
                <div className="w-10 h-10 rounded-xl bg-status-confirmed/20 flex items-center justify-center">
                  <Package className="w-5 h-5 text-status-confirmed" />
                </div>
                <div>
                  <p className="text-xs text-muted-foreground font-medium">Orders</p>
                  <p className="text-2xl font-bold">{stats.totalOrders}</p>
                </div>
              </div>
            </CardContent>
          </Card>

          <Card className="border-0 shadow-md bg-gradient-to-br from-status-pending/5 to-status-pending/10">
            <CardContent className="p-4">
              <div className="flex items-center gap-3">
                <div className="w-10 h-10 rounded-xl bg-status-pending/20 flex items-center justify-center">
                  <Clock className="w-5 h-5 text-status-pending" />
                </div>
                <div>
                  <p className="text-xs text-muted-foreground font-medium">Active</p>
                  <p className="text-2xl font-bold text-status-pending">{stats.activeOrders}</p>
                </div>
              </div>
            </CardContent>
          </Card>

          <Card className="border-0 shadow-md bg-gradient-to-br from-status-delivered/5 to-status-delivered/10">
            <CardContent className="p-4">
              <div className="flex items-center gap-3">
                <div className="w-10 h-10 rounded-xl bg-status-delivered/20 flex items-center justify-center">
                  <TrendingUp className="w-5 h-5 text-status-delivered" />
                </div>
                <div>
                  <p className="text-xs text-muted-foreground font-medium">Revenue</p>
                  <p className="text-2xl font-bold text-status-delivered">{stats.totalRevenue.toFixed(0)}</p>
                </div>
              </div>
            </CardContent>
          </Card>

          <Card className="border-0 shadow-md bg-gradient-to-br from-primary/10 to-accent/10">
            <CardContent className="p-4">
              <div className="flex items-center gap-3">
                <div className="w-10 h-10 rounded-xl bg-primary/20 flex items-center justify-center">
                  <Bike className="w-5 h-5 text-primary" />
                </div>
                <div>
                  <p className="text-xs text-muted-foreground font-medium">Delivery Fee</p>
                  <p className="text-2xl font-bold text-primary">{stats.deliveryFeeEarnings.toFixed(0)}</p>
                </div>
              </div>
            </CardContent>
          </Card>

          <Card className="border-0 shadow-md bg-gradient-to-br from-accent/10 to-status-confirmed/10">
            <CardContent className="p-4">
              <div className="flex items-center gap-3">
                <div className="w-10 h-10 rounded-xl bg-accent/20 flex items-center justify-center">
                  <IndianRupee className="w-5 h-5 text-accent" />
                </div>
                <div>
                  <p className="text-xs text-muted-foreground font-medium">Platform Fee</p>
                  <p className="text-2xl font-bold text-accent">{stats.platformFeeEarnings.toFixed(0)}</p>
                </div>
              </div>
            </CardContent>
          </Card>

          <Card className="border-0 shadow-md bg-gradient-to-br from-status-preparing/5 to-status-preparing/10">
            <CardContent className="p-4">
              <div className="flex items-center gap-3">
                <div className="w-10 h-10 rounded-xl bg-status-preparing/20 flex items-center justify-center">
                  <Users className="w-5 h-5 text-status-preparing" />
                </div>
                <div>
                  <p className="text-xs text-muted-foreground font-medium">Partners</p>
                  <p className="text-2xl font-bold text-status-preparing">{stats.totalPartners}</p>
                </div>
              </div>
            </CardContent>
          </Card>
        </div>

        <Tabs defaultValue="shops" className="space-y-6">
          <TabsList className="grid w-full grid-cols-6">
            <TabsTrigger value="shops" className="gap-1 text-xs">
              <Store className="w-4 h-4" />
              <span className="hidden sm:inline">Shops</span>
            </TabsTrigger>
            <TabsTrigger value="orders" className="gap-1 text-xs">
              <Package className="w-4 h-4" />
              <span className="hidden sm:inline">Orders</span>
            </TabsTrigger>
            <TabsTrigger value="partners" className="gap-1 text-xs">
              <Bike className="w-4 h-4" />
              <span className="hidden sm:inline">Partners</span>
            </TabsTrigger>
            <TabsTrigger value="users" className="gap-1 text-xs">
              <Users className="w-4 h-4" />
              <span className="hidden sm:inline">Users</span>
            </TabsTrigger>
            <TabsTrigger value="promotions" className="gap-1 text-xs">
              <Sparkles className="w-4 h-4" />
              <span className="hidden sm:inline">Promos</span>
            </TabsTrigger>
            <TabsTrigger value="coupons" className="gap-1 text-xs">
              <Tag className="w-4 h-4" />
              <span className="hidden sm:inline">Coupons</span>
            </TabsTrigger>
          </TabsList>

          <TabsContent value="shops">
            <Card className="border-0 shadow-lg">
              <CardHeader className="pb-3">
                <div className="flex flex-col sm:flex-row sm:items-center justify-between gap-3">
                  <CardTitle className="flex items-center gap-2 text-lg">
                    <Store className="w-5 h-5 text-primary" />
                    Shop Management ({filteredRestaurants.length})
                  </CardTitle>
                  <div className="relative">
                    <Search className="absolute left-3 top-1/2 -translate-y-1/2 w-4 h-4 text-muted-foreground" />
                    <Input
                      placeholder="Search shops..."
                      value={searchShops}
                      onChange={(e) => setSearchShops(e.target.value)}
                      className="pl-9 h-9 w-full sm:w-64"
                    />
                  </div>
                </div>
              </CardHeader>
              <CardContent className="p-0">
                <ScrollArea className="h-[500px]">
                  <div className="p-4 space-y-3">
                    {filteredRestaurants.map((restaurant) => (
                      <div
                        key={restaurant.id}
                        className="p-3 rounded-xl border border-border bg-card hover:bg-secondary/30 transition-all"
                      >
                        <div className="flex items-start justify-between gap-3">
                          <div className="flex-1 min-w-0">
                            <div className="flex items-center gap-2">
                              <h4 className="font-semibold truncate">{restaurant.name}</h4>
                              <span className={`text-xs px-2 py-0.5 rounded-full ${restaurant.is_open
                                ? 'bg-accent/20 text-accent'
                                : 'bg-muted text-muted-foreground'
                                }`}>
                                {restaurant.is_open ? 'Open' : 'Closed'}
                              </span>
                            </div>
                            <p className="text-xs text-muted-foreground mt-0.5">{restaurant.cuisine_type}</p>
                          </div>
                          <div className="flex items-center gap-2">
                            <Switch
                              checked={restaurant.is_verified}
                              onCheckedChange={(checked) => toggleVerification(restaurant.id, checked)}
                            />
                          </div>
                        </div>

                        <div className="flex items-center gap-2 mt-3 pt-3 border-t border-border/50">
                          <Input
                            type="number"
                            placeholder={`${restaurant.commission_rate}%`}
                            value={commissionUpdates[restaurant.id] || ''}
                            onChange={(e) => setCommissionUpdates((prev) => ({
                              ...prev,
                              [restaurant.id]: e.target.value,
                            }))}
                            className="w-20 h-8 text-sm"
                          />
                          <Button
                            size="sm"
                            variant="outline"
                            onClick={() => updateCommission(restaurant.id)}
                            disabled={!commissionUpdates[restaurant.id]}
                            className="h-8"
                          >
                            <Percent className="w-3 h-3 mr-1" />
                            Set
                          </Button>
                          <span className="text-xs text-muted-foreground ml-auto">
                            Current: {restaurant.commission_rate}%
                          </span>
                        </div>
                      </div>
                    ))}
                  </div>
                </ScrollArea>
              </CardContent>
            </Card>
          </TabsContent>

          <TabsContent value="orders">
            <Card className="border-0 shadow-lg">
              <CardHeader className="pb-3">
                <div className="flex flex-col sm:flex-row sm:items-center justify-between gap-3">
                  <CardTitle className="flex items-center gap-2 text-lg">
                    <Package className="w-5 h-5 text-status-confirmed" />
                    Orders ({filteredOrders.length})
                  </CardTitle>
                  <div className="flex flex-col sm:flex-row gap-2">
                    <div className="relative">
                      <Search className="absolute left-3 top-1/2 -translate-y-1/2 w-4 h-4 text-muted-foreground" />
                      <Input
                        placeholder="Search orders..."
                        value={searchOrders}
                        onChange={(e) => setSearchOrders(e.target.value)}
                        className="pl-9 h-9 w-full sm:w-48"
                      />
                    </div>
                    <Select value={orderStatusFilter} onValueChange={setOrderStatusFilter}>
                      <SelectTrigger className="h-9 w-full sm:w-36">
                        <Filter className="w-3.5 h-3.5 mr-1" />
                        <SelectValue placeholder="Status" />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="all">All Status</SelectItem>
                        <SelectItem value="pending">Pending</SelectItem>
                        <SelectItem value="confirmed">Confirmed</SelectItem>
                        <SelectItem value="preparing">Preparing</SelectItem>
                        <SelectItem value="ready_for_pickup">Ready</SelectItem>
                        <SelectItem value="picked_up">Picked Up</SelectItem>
                        <SelectItem value="on_the_way">On The Way</SelectItem>
                        <SelectItem value="delivered">Delivered</SelectItem>
                        <SelectItem value="cancelled">Cancelled</SelectItem>
                      </SelectContent>
                    </Select>
                  </div>
                </div>
              </CardHeader>
              <CardContent className="p-0">
                <ScrollArea className="h-[500px]">
                  <div className="p-4 space-y-3">
                    {filteredOrders.map((order) => (
                      <div
                        key={order.id}
                        className="p-3 rounded-xl border border-border bg-card hover:bg-secondary/30 transition-all"
                      >
                        <div className="flex items-start justify-between gap-3">
                          <div className="flex-1 min-w-0">
                            <div className="flex items-center gap-2">
                              <span className="font-mono text-sm font-medium">#{order.id.slice(0, 8)}</span>
                              <StatusBadge status={order.status as OrderStatus} />
                            </div>
                            <p className="text-sm mt-1">{order.restaurants?.name || 'Unknown'}</p>
                            <p className="text-xs text-muted-foreground">
                              {format(new Date(order.created_at), 'MMM d, h:mm a')}
                            </p>
                          </div>
                          <div className="text-right">
                            <p className="font-bold text-primary">{Number(order.total_amount).toFixed(0)}</p>
                            {!['delivered', 'cancelled'].includes(order.status) && (
                              <AlertDialog>
                                <AlertDialogTrigger asChild>
                                  <Button variant="ghost" size="sm" className="h-7 text-xs text-destructive hover:text-destructive mt-1">
                                    <X className="w-3 h-3 mr-1" />
                                    Cancel
                                  </Button>
                                </AlertDialogTrigger>
                                <AlertDialogContent>
                                  <AlertDialogHeader>
                                    <AlertDialogTitle>Cancel Order</AlertDialogTitle>
                                    <AlertDialogDescription>
                                      Are you sure you want to cancel this order? This cannot be undone.
                                    </AlertDialogDescription>
                                  </AlertDialogHeader>
                                  <AlertDialogFooter>
                                    <AlertDialogCancel>Keep</AlertDialogCancel>
                                    <AlertDialogAction
                                      onClick={() => cancelOrder(order.id)}
                                      className="bg-destructive text-destructive-foreground hover:bg-destructive/90"
                                    >
                                      Cancel Order
                                    </AlertDialogAction>
                                  </AlertDialogFooter>
                                </AlertDialogContent>
                              </AlertDialog>
                            )}
                          </div>
                        </div>
                      </div>
                    ))}
                  </div>
                </ScrollArea>
              </CardContent>
            </Card>
          </TabsContent>

          <TabsContent value="partners">
            <Card className="border-0 shadow-lg">
              <CardHeader className="pb-3">
                <CardTitle className="flex items-center gap-2 text-lg">
                  <Bike className="w-5 h-5 text-status-preparing" />
                  Delivery Partners ({deliveryPartners.length})
                </CardTitle>
              </CardHeader>
              <CardContent>
                <div className="grid sm:grid-cols-2 lg:grid-cols-3 gap-3">
                  {deliveryPartners.map((partner) => (
                    <div
                      key={partner.id}
                      key={partner.id}
                      className="p-4 rounded-xl border border-border bg-card hover:bg-secondary/30 transition-all"
                    >
                      <div className="flex items-start gap-3">
                        <div className={`w-12 h-12 rounded-full flex items-center justify-center ${partner.is_available ? 'bg-accent/20' : 'bg-muted'
                          }`}>
                          <Bike className={`w-6 h-6 ${partner.is_available ? 'text-accent' : 'text-muted-foreground'}`} />
                        </div>
                        <div className="flex-1 min-w-0">
                          <div className="flex items-center justify-between">
                            <p className="text-sm font-semibold">Partner #{partner.id.slice(0, 6)}</p>
                            <span className={`text-xs px-2 py-0.5 rounded-full ${partner.is_available
                              ? 'bg-accent/20 text-accent'
                              : 'bg-muted text-muted-foreground'
                              }`}>
                              {partner.is_available ? 'Online' : 'Offline'}
                            </span>
                          </div>
                          {partner.phone && (
                            <a
                              href={`tel:${partner.phone}`}
                              className="flex items-center gap-1 text-xs text-primary hover:underline mt-1"
                            >
                              <Phone className="w-3 h-3" />
                              {partner.phone}
                            </a>
                          )}
                          {partner.vehicle_type && (
                            <p className="text-xs text-muted-foreground mt-1 flex items-center gap-1">
                              <Bike className="w-3 h-3" />
                              {partner.vehicle_type}
                            </p>
                          )}
                          <p className="text-xs text-muted-foreground mt-1">
                            Joined: {format(new Date(partner.created_at), 'MMM d, yyyy')}
                          </p>
                        </div>
                      </div>
                    </div>
                  ))}
                  {deliveryPartners.length === 0 && (
                    <div className="col-span-full text-center py-8 text-muted-foreground">
                      <Bike className="w-10 h-10 mx-auto mb-2 opacity-50" />
                      <p>No delivery partners registered yet</p>
                    </div>
                  )}
                </div>
              </CardContent>
            </Card>
          </TabsContent>

          <TabsContent value="users">
            <Card className="border-0 shadow-lg">
              <CardHeader className="pb-3">
                <div className="flex flex-col sm:flex-row sm:items-center justify-between gap-3">
                  <CardTitle className="flex items-center gap-2 text-lg">
                    <Users className="w-5 h-5 text-primary" />
                    User Role Management ({filteredUsers.length})
                  </CardTitle>
                  <div className="relative">
                    <Search className="absolute left-3 top-1/2 -translate-y-1/2 w-4 h-4 text-muted-foreground" />
                    <Input
                      placeholder="Search users..."
                      value={searchUsers}
                      onChange={(e) => setSearchUsers(e.target.value)}
                      className="pl-9 h-9 w-full sm:w-64"
                    />
                  </div>
                </div>
              </CardHeader>
              <CardContent className="p-0">
                <ScrollArea className="h-[500px]">
                  <div className="p-4 space-y-3">
                    {filteredUsers.map((u) => (
                      <div
                        key={u.id}
                        className="p-4 rounded-xl border border-border bg-card hover:bg-secondary/30 transition-all"
                      >
                        <div className="flex items-start justify-between gap-3">
                          <div className="flex-1 min-w-0">
                            <h4 className="font-semibold">{u.full_name || 'No Name'}</h4>
                            <p className="text-sm text-muted-foreground">{u.email}</p>
                            <div className="flex flex-wrap gap-2 mt-2">
                              {u.roles.map((role) => (
                                <span
                                  key={role}
                                  className="inline-flex items-center gap-1 text-xs px-2 py-1 rounded-full bg-primary/10 text-primary"
                                >
                                  {role}
                                  <button
                                    onClick={() => removeRoleFromUser(u.id, role)}
                                    className="hover:text-destructive"
                                  >
                                    <X className="w-3 h-3" />
                                  </button>
                                </span>
                              ))}
                              {u.roles.length === 0 && (
                                <span className="text-xs text-muted-foreground">No roles assigned</span>
                              )}
                            </div>
                          </div>
                        </div>

                        <div className="flex items-center gap-2 mt-3 pt-3 border-t border-border/50">
                          <Select
                            value={selectedRole[u.id] || ''}
                            onValueChange={(value) => setSelectedRole(prev => ({ ...prev, [u.id]: value as AppRole }))}
                          >
                            <SelectTrigger className="w-40 h-8">
                              <SelectValue placeholder="Select role" />
                            </SelectTrigger>
                            <SelectContent>
                              <SelectItem value="super_admin">Super Admin</SelectItem>
                              <SelectItem value="restaurant_owner">Restaurant Owner</SelectItem>
                              <SelectItem value="delivery_partner">Delivery Partner</SelectItem>
                              <SelectItem value="customer">Customer</SelectItem>
                            </SelectContent>
                          </Select>
                          <Button
                            size="sm"
                            onClick={() => addRoleToUser(u.id)}
                            disabled={!selectedRole[u.id]}
                            className="h-8"
                          >
                            <UserPlus className="w-3 h-3 mr-1" />
                            Add Role
                          </Button>
                        </div>
                      </div>
                    ))}
                  </div>
                </ScrollArea>
              </CardContent>
            </Card>
          </TabsContent>

          <TabsContent value="promotions">
            <PromotionsManager />
          </TabsContent>

          <TabsContent value="coupons">
            <CouponsManager />
          </TabsContent>
        </Tabs>
      </div>
    </div>
  );
}

import { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { supabase } from '@/integrations/supabase/client';
import { useAuth } from '@/hooks/useAuth';
import { Order, OrderStatus, DeliveryPartner } from '@/types/database';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Switch } from '@/components/ui/switch';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { StatusBadge } from '@/components/StatusBadge';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
  AlertDialogTrigger,
} from '@/components/ui/alert-dialog';
import {
  Bike,
  ArrowLeft,
  MapPin,
  Navigation,
  Package,
  Clock,
  CheckCircle,
  Phone,
  Key,
  Banknote,
  Smartphone,
  AlertCircle,
  Shield,
  History,
  Wallet,
  IndianRupee,
  TrendingUp,
  User,
  RefreshCw,
  ExternalLink,
  X,
  BarChart3,
  FileText,
  Headphones,
  Star,
  Calendar,
  Percent,
  Award
} from 'lucide-react';
import { toast } from 'sonner';
import { format } from 'date-fns';
import { useDeliveryPartnerNotifications } from '@/hooks/useOrderNotifications';

interface OrderWithDetails extends Order {
  restaurants: { name: string; address: string; phone: string | null } | null;
  customer_profile?: { full_name: string | null; phone: string | null } | null;
}

// Helper to generate short order ID
const getShortOrderId = (id: string) => {
  return `#${id.slice(0, 8).toUpperCase()}`;
};

export default function DeliveryDashboard() {
  const { user, loading: authLoading } = useAuth();
  const navigate = useNavigate();
  const [deliveryPartner, setDeliveryPartner] = useState<DeliveryPartner | null>(null);
  const [profile, setProfile] = useState<{ full_name: string | null; phone: string | null } | null>(null);
  const [availableOrders, setAvailableOrders] = useState<OrderWithDetails[]>([]);
  const [myOrders, setMyOrders] = useState<OrderWithDetails[]>([]);
  const [orderHistory, setOrderHistory] = useState<OrderWithDetails[]>([]);
  const [earnings, setEarnings] = useState({ today: 0, week: 0, total: 0, pending: 0 });
  const [avgRating, setAvgRating] = useState<number | null>(null);
  const [totalRatings, setTotalRatings] = useState(0);
  const [loading, setLoading] = useState(true);
  const [refreshing, setRefreshing] = useState(false);
  const [showRegister, setShowRegister] = useState(false);
  const [deliveryOtpInputs, setDeliveryOtpInputs] = useState<Record<string, string>>({});
  const [deliveryOtpErrors, setDeliveryOtpErrors] = useState<Record<string, boolean>>({});
  const [pickupOtpInputs, setPickupOtpInputs] = useState<Record<string, string>>({});
  const [pickupOtpErrors, setPickupOtpErrors] = useState<Record<string, boolean>>({});

  // Phone verification states
  const [phoneNumber, setPhoneNumber] = useState('');
  const [verificationOtp, setVerificationOtp] = useState('');
  const [showVerificationInput, setShowVerificationInput] = useState(false);
  const [generatedOtp, setGeneratedOtp] = useState('');

  useEffect(() => {
    if (!authLoading) {
      if (!user) {
        navigate('/auth');
        return;
      }
      fetchData();
    }
  }, [user, authLoading]);

  // Real-time order updates
  useEffect(() => {
    if (!deliveryPartner) return;

    const channel = supabase
      .channel('delivery-orders')
      .on(
        'postgres_changes',
        {
          event: '*',
          schema: 'public',
          table: 'orders'
        },
        () => {
          fetchData();
        }
      )
      .subscribe();

    return () => {
      supabase.removeChannel(channel);
    };
  }, [deliveryPartner]);

  // Sound notifications for available orders
  useDeliveryPartnerNotifications(deliveryPartner?.id, deliveryPartner?.is_available || false);

  const fetchData = async () => {
    try {
      // Check if user is a delivery partner
      const { data: partnerData } = await supabase
        .from('delivery_partners')
        .select('*')
        .eq('user_id', user!.id)
        .maybeSingle();

      // Fetch user profile
      const { data: profileData } = await supabase
        .from('profiles')
        .select('full_name, phone')
        .eq('id', user!.id)
        .maybeSingle();

      if (profileData) setProfile(profileData);

      if (partnerData) {
        setDeliveryPartner(partnerData);

        // Fetch available orders (ready_for_pickup with no delivery partner)
        // Include pickup_otp for OTP verification
        const { data: availableData } = await supabase
          .from('orders')
          .select('*, restaurants(name, address, phone)')
          .eq('status', 'ready_for_pickup')
          .is('delivery_partner_id', null)
          .order('created_at', { ascending: false });

        if (availableData) {
          // Fetch customer profiles for available orders
          const customerIds = availableData.map(o => o.customer_id);
          let customerProfiles: { id: string; full_name: string | null; phone: string | null }[] = [];

          if (customerIds.length > 0) {
            const { data: profilesData } = await supabase
              .from('profiles')
              .select('id, full_name, phone')
              .in('id', customerIds);
            customerProfiles = profilesData || [];
          }

          const ordersWithProfiles = availableData.map(order => ({
            ...order,
            customer_profile: customerProfiles.find(p => p.id === order.customer_id) || null
          }));
          setAvailableOrders(ordersWithProfiles);
        }

        // Fetch my orders with customer info
        const { data: myOrdersData } = await supabase
          .from('orders')
          .select('*, restaurants(name, address, phone)')
          .eq('delivery_partner_id', partnerData.id)
          .in('status', ['picked_up', 'on_the_way'])
          .order('created_at', { ascending: false });

        if (myOrdersData) {
          // Fetch customer profiles for my orders
          const customerIds = myOrdersData.map(o => o.customer_id);
          let customerProfiles: { id: string; full_name: string | null; phone: string | null }[] = [];

          if (customerIds.length > 0) {
            const { data: profilesData } = await supabase
              .from('profiles')
              .select('id, full_name, phone')
              .in('id', customerIds);
            customerProfiles = profilesData || [];
          }

          const ordersWithProfiles = myOrdersData.map(order => ({
            ...order,
            customer_profile: customerProfiles.find(p => p.id === order.customer_id) || null
          }));
          setMyOrders(ordersWithProfiles);
        }

        // Fetch order history (completed/cancelled)
        const { data: historyData } = await supabase
          .from('orders')
          .select('*, restaurants(name, address, phone)')
          .eq('delivery_partner_id', partnerData.id)
          .in('status', ['delivered', 'cancelled'])
          .order('created_at', { ascending: false })
          .limit(50);

        if (historyData) setOrderHistory(historyData);

        // Calculate earnings
        const deliveredOrders = historyData?.filter(o => o.status === 'delivered') || [];
        const today = new Date();
        today.setHours(0, 0, 0, 0);
        const weekAgo = new Date(today);
        weekAgo.setDate(weekAgo.getDate() - 7);

        const deliveryFee = 30; // Fixed delivery fee per order
        const todayEarnings = deliveredOrders
          .filter(o => new Date(o.created_at) >= today)
          .length * deliveryFee;
        const weekEarnings = deliveredOrders
          .filter(o => new Date(o.created_at) >= weekAgo)
          .length * deliveryFee;
        const totalEarnings = deliveredOrders.length * deliveryFee;
        const pendingSettlement = Math.floor(totalEarnings * 0.2); // 20% pending

        setEarnings({
          today: todayEarnings,
          week: weekEarnings,
          total: totalEarnings,
          pending: pendingSettlement
        });

        // Fetch ratings for this delivery partner
        const { data: ratingsData } = await supabase
          .from('order_ratings')
          .select('rating')
          .eq('delivery_partner_id', partnerData.id);

        if (ratingsData && ratingsData.length > 0) {
          const total = ratingsData.reduce((sum, r) => sum + r.rating, 0);
          setAvgRating(total / ratingsData.length);
          setTotalRatings(ratingsData.length);
        } else {
          setAvgRating(null);
          setTotalRatings(0);
        }
      } else {
        setShowRegister(true);
      }
    } catch (error) {
      console.error('Error fetching data:', error);
      toast.error('Failed to load data');
    }

    setLoading(false);
    setRefreshing(false);
  };

  const handleRefresh = () => {
    setRefreshing(true);
    fetchData();
  };

  const sendVerificationOtp = () => {
    if (!phoneNumber || phoneNumber.length < 10) {
      toast.error('Please enter a valid phone number');
      return;
    }

    // Generate OTP (demo mode - show in app)
    const otp = Math.floor(1000 + Math.random() * 9000).toString();
    setGeneratedOtp(otp);
    setShowVerificationInput(true);
    toast.success(`Demo OTP: ${otp} (In production, this would be sent via SMS)`);
  };

  const verifyPhoneOtp = () => {
    if (verificationOtp === generatedOtp) {
      registerAsPartner();
    } else {
      toast.error('Invalid OTP. Please try again.');
    }
  };

  const registerAsPartner = async () => {
    const { data, error } = await supabase
      .from('delivery_partners')
      .insert({
        user_id: user!.id,
        is_available: false,
        vehicle_type: 'Bike',
        phone: phoneNumber,
        phone_verified: true,
      })
      .select()
      .single();

    if (error) {
      toast.error(error.message);
    } else {
      // Use secure RPC to assign delivery_partner role
      await supabase.rpc('request_delivery_partner_role');

      setDeliveryPartner(data);
      setShowRegister(false);
      toast.success('Registered as delivery partner!');
    }
  };

  const toggleAvailability = async () => {
    if (!deliveryPartner) return;

    const { error } = await supabase
      .from('delivery_partners')
      .update({ is_available: !deliveryPartner.is_available })
      .eq('id', deliveryPartner.id);

    if (error) {
      toast.error('Failed to update availability');
    } else {
      setDeliveryPartner({ ...deliveryPartner, is_available: !deliveryPartner.is_available });
      toast.success(deliveryPartner.is_available ? 'You are now offline' : 'You are now online');
      if (!deliveryPartner.is_available) fetchData(); // Refresh available orders
    }
  };

  const verifyPickupAndAccept = async (order: OrderWithDetails) => {
    if (!deliveryPartner) return;

    // Check if partner already has an active order
    const { data: hasActive } = await supabase.rpc('partner_has_active_order');
    if (hasActive) {
      toast.error('Complete your current delivery first. One order at a time.');
      return;
    }

    const otpValue = pickupOtpInputs[order.id] || '';

    // Use secure server-side RPC for OTP verification
    const { data, error } = await supabase.rpc('verify_pickup_and_accept_order', {
      _order_id: order.id,
      _pickup_otp: otpValue
    });

    if (error) {
      toast.error('Failed to accept order');
      return;
    }

    if (data) {
      toast.success('Order accepted!');
      setPickupOtpInputs(prev => ({ ...prev, [order.id]: '' }));
      setPickupOtpErrors(prev => ({ ...prev, [order.id]: false }));
      fetchData();
    } else {
      setPickupOtpErrors(prev => ({ ...prev, [order.id]: true }));
      toast.error('Invalid pickup OTP. Please check with the restaurant.');
    }
  };

  const updateOrderStatus = async (orderId: string, status: OrderStatus) => {
    const { error } = await supabase
      .from('orders')
      .update({ status })
      .eq('id', orderId);

    if (error) {
      toast.error('Failed to update status');
    } else {
      toast.success('Status updated');
      setDeliveryOtpInputs(prev => ({ ...prev, [orderId]: '' }));
      setDeliveryOtpErrors(prev => ({ ...prev, [orderId]: false }));
      fetchData();
    }
  };

  const verifyAndDeliver = async (order: OrderWithDetails) => {
    const otpValue = deliveryOtpInputs[order.id] || '';

    // Use secure server-side RPC for OTP verification
    const { data, error } = await supabase.rpc('verify_delivery_and_complete', {
      _order_id: order.id,
      _delivery_otp: otpValue
    });

    if (error) {
      toast.error('Failed to update status');
      return;
    }

    if (data) {
      toast.success('Order delivered!');
      setDeliveryOtpInputs(prev => ({ ...prev, [order.id]: '' }));
      setDeliveryOtpErrors(prev => ({ ...prev, [order.id]: false }));
      fetchData();
    } else {
      setDeliveryOtpErrors(prev => ({ ...prev, [order.id]: true }));
      toast.error('Invalid OTP. Please check with the customer.');
    }
  };

  const releaseOrderToPool = async (orderId: string) => {
    // Use secure RPC to release order back to pool for another partner
    const { data, error } = await supabase.rpc('release_order_to_pool', {
      _order_id: orderId
    });

    if (error) {
      toast.error('Failed to release order');
    } else if (data) {
      toast.success('Order released for another partner');
      setDeliveryOtpInputs(prev => ({ ...prev, [orderId]: '' }));
      setDeliveryOtpErrors(prev => ({ ...prev, [orderId]: false }));
      setPickupOtpInputs(prev => ({ ...prev, [orderId]: '' }));
      setPickupOtpErrors(prev => ({ ...prev, [orderId]: false }));
      fetchData();
    } else {
      toast.error('Could not release order');
    }
  };

  if (authLoading || loading) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center">
        <div className="animate-pulse text-muted-foreground">Loading...</div>
      </div>
    );
  }

  if (showRegister) {
    return (
      <div className="min-h-screen bg-background">
        <header className="sticky top-0 z-50 bg-card/95 backdrop-blur-md border-b border-border">
          <div className="container mx-auto px-4 py-4">
            <Link to="/" className="flex items-center gap-2 text-foreground hover:text-primary transition-colors">
              <ArrowLeft className="w-5 h-5" />
              <span>Back</span>
            </Link>
          </div>
        </header>
        <div className="container mx-auto px-4 py-8 max-w-md">
          <Card className="border-0 shadow-lg">
            <CardContent className="py-8">
              <div className="text-center mb-6">
                <div className="w-20 h-20 bg-primary/10 rounded-full flex items-center justify-center mx-auto mb-4">
                  <Bike className="w-10 h-10 text-primary" />
                </div>
                <h2 className="text-2xl font-bold mb-2">Become a Delivery Partner</h2>
                <p className="text-muted-foreground">
                  Earn money delivering food to customers in your area
                </p>
              </div>

              {!showVerificationInput ? (
                <div className="space-y-4">
                  <div className="space-y-2">
                    <Label htmlFor="phone">Phone Number</Label>
                    <div className="relative">
                      <Phone className="absolute left-3 top-3 h-4 w-4 text-muted-foreground" />
                      <Input
                        id="phone"
                        type="tel"
                        placeholder="+1 234 567 8900"
                        value={phoneNumber}
                        onChange={(e) => setPhoneNumber(e.target.value)}
                        className="pl-10"
                      />
                    </div>
                  </div>
                  <Button onClick={sendVerificationOtp} className="w-full" size="lg">
                    <Shield className="w-4 h-4 mr-2" />
                    Send Verification OTP
                  </Button>
                </div>
              ) : (
                <div className="space-y-4">
                  <div className="p-3 bg-accent/10 rounded-lg border border-accent/20 text-center">
                    <p className="text-sm text-accent font-medium">Demo OTP: {generatedOtp}</p>
                    <p className="text-xs text-muted-foreground mt-1">In production, this would be sent via SMS</p>
                  </div>
                  <div className="space-y-2">
                    <Label htmlFor="otp">Enter OTP</Label>
                    <Input
                      id="otp"
                      type="text"
                      placeholder="Enter 4-digit OTP"
                      value={verificationOtp}
                      onChange={(e) => setVerificationOtp(e.target.value.replace(/\D/g, '').slice(0, 4))}
                      className="font-mono text-center text-lg tracking-widest"
                      maxLength={4}
                    />
                  </div>
                  <Button onClick={verifyPhoneOtp} className="w-full" size="lg" disabled={verificationOtp.length !== 4}>
                    <CheckCircle className="w-4 h-4 mr-2" />
                    Verify & Register
                  </Button>
                  <Button variant="outline" onClick={() => setShowVerificationInput(false)} className="w-full">
                    Change Phone Number
                  </Button>
                </div>
              )}
            </CardContent>
          </Card>
        </div>
      </div>
    );
  }

  const currentOrder = myOrders[0];

  return (
    <div className="min-h-screen bg-background">
      <header className="sticky top-0 z-50 bg-card/95 backdrop-blur-md border-b border-border">
        <div className="container mx-auto px-4 py-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-4">
              <Link to="/" className="text-foreground hover:text-primary transition-colors">
                <ArrowLeft className="w-5 h-5" />
              </Link>
              <div className="flex items-center gap-2">
                <Bike className="w-5 h-5 text-primary hidden md:block" />
                <div>
                  <h1 className="text-lg font-bold leading-tight">Delivery Dashboard</h1>
                  {profile?.full_name && (
                    <p className="text-xs text-muted-foreground flex items-center gap-1">
                      <User className="w-3 h-3" /> {profile.full_name}
                    </p>
                  )}
                </div>
              </div>
            </div>
            <div className="flex items-center gap-3">
              <Button
                variant="ghost"
                size="icon"
                onClick={handleRefresh}
                disabled={refreshing}
                className="h-8 w-8"
              >
                <RefreshCw className={`w-4 h-4 ${refreshing ? 'animate-spin' : ''}`} />
              </Button>
              <div className="flex items-center gap-2">
                <span className={`text-sm ${deliveryPartner?.is_available ? 'text-accent' : 'text-muted-foreground'}`}>
                  {deliveryPartner?.is_available ? 'Online' : 'Offline'}
                </span>
                <Switch
                  checked={deliveryPartner?.is_available || false}
                  onCheckedChange={toggleAvailability}
                />
              </div>
            </div>
          </div>
        </div>
      </header>

      <div className="container mx-auto px-4 py-6">
        <Tabs defaultValue="active" className="space-y-6">
          <TabsList className="grid w-full grid-cols-4">
            <TabsTrigger value="active" className="gap-2">
              <Package className="w-4 h-4" />
              Active
            </TabsTrigger>
            <TabsTrigger value="history" className="gap-2">
              <History className="w-4 h-4" />
              History
            </TabsTrigger>
            <TabsTrigger value="earnings" className="gap-2">
              <Wallet className="w-4 h-4" />
              Earnings
            </TabsTrigger>
            <TabsTrigger value="profile" className="gap-2">
              <User className="w-4 h-4" />
              Profile
            </TabsTrigger>
          </TabsList>

          <TabsContent value="active" className="space-y-6">
            {/* Quick Stats */}
            <div className="grid grid-cols-3 gap-3">
              <Card className="border-0 shadow-sm">
                <CardContent className="p-3 text-center">
                  <div className={`w-3 h-3 rounded-full mx-auto mb-1 ${deliveryPartner?.is_available ? 'bg-accent animate-pulse' : 'bg-muted-foreground'}`} />
                  <p className="text-xs text-muted-foreground">Status</p>
                  <p className={`font-semibold text-sm ${deliveryPartner?.is_available ? 'text-accent' : 'text-muted-foreground'}`}>
                    {deliveryPartner?.is_available ? 'Online' : 'Offline'}
                  </p>
                </CardContent>
              </Card>
              <Card className="border-0 shadow-sm">
                <CardContent className="p-3 text-center">
                  <Package className="w-4 h-4 mx-auto mb-1 text-primary" />
                  <p className="text-xs text-muted-foreground">Available</p>
                  <p className="font-semibold text-sm">{availableOrders.length} orders</p>
                </CardContent>
              </Card>
              <Card className="border-0 shadow-sm">
                <CardContent className="p-3 text-center">
                  <CheckCircle className="w-4 h-4 mx-auto mb-1 text-status-delivered" />
                  <p className="text-xs text-muted-foreground">Today</p>
                  <p className="font-semibold text-sm">{earnings.today}</p>
                </CardContent>
              </Card>
            </div>

            {/* Current Delivery */}
            {currentOrder && (
              <Card className="border-0 shadow-lg bg-gradient-to-r from-primary/5 to-accent/5">
                <CardHeader>
                  <CardTitle className="flex items-center justify-between">
                    <div className="flex items-center gap-2">
                      <Package className="w-5 h-5 text-primary" />
                      Current Delivery
                    </div>
                    <span className="text-sm font-mono bg-secondary px-2 py-1 rounded">
                      {getShortOrderId(currentOrder.id)}
                    </span>
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="space-y-4">
                    <div className="flex items-start justify-between">
                      <div>
                        <p className="font-semibold text-lg">{currentOrder.restaurants?.name}</p>
                        <StatusBadge status={currentOrder.status as OrderStatus} />
                      </div>
                      <div className="text-right">
                        <p className="font-bold text-primary">{Number(currentOrder.total_amount).toFixed(2)}</p>
                        <span className="flex items-center gap-1 text-xs mt-1">
                          {currentOrder.payment_method === 'cod' ? (
                            <span className="flex items-center gap-1 bg-accent/10 text-accent px-2 py-1 rounded-full">
                              <Banknote className="w-3 h-3" /> COD
                            </span>
                          ) : (
                            <span className="flex items-center gap-1 bg-primary/10 text-primary px-2 py-1 rounded-full">
                              <Smartphone className="w-3 h-3" /> GPay
                            </span>
                          )}
                        </span>
                      </div>
                    </div>

                    {/* Customer Info */}
                    <div className="flex items-center justify-between p-3 bg-card rounded-lg border border-primary/20">
                      <div className="flex items-center gap-3">
                        <div className="w-10 h-10 rounded-full bg-primary/10 flex items-center justify-center">
                          <User className="w-5 h-5 text-primary" />
                        </div>
                        <div>
                          <p className="font-medium">{currentOrder.customer_profile?.full_name || 'Customer'}</p>
                          <p className="text-sm text-muted-foreground">Customer</p>
                        </div>
                      </div>
                      {currentOrder.customer_profile?.phone && (
                        <a
                          href={`tel:${currentOrder.customer_profile.phone}`}
                          className="flex items-center gap-2 bg-accent text-accent-foreground px-4 py-2 rounded-lg font-medium hover:bg-accent/90 transition-colors"
                        >
                          <Phone className="w-4 h-4" />
                          Call
                        </a>
                      )}
                    </div>

                    <div className="grid gap-3">
                      <div className="flex items-start gap-3 p-3 bg-card rounded-lg">
                        <MapPin className="w-5 h-5 text-accent mt-0.5" />
                        <div className="flex-1">
                          <p className="text-sm text-muted-foreground">Pickup from</p>
                          <p className="font-medium">{currentOrder.restaurants?.address}</p>
                          <div className="flex items-center gap-3 mt-2">
                            {currentOrder.restaurants?.phone && (
                              <a href={`tel:${currentOrder.restaurants.phone}`} className="flex items-center gap-1 text-primary text-sm hover:underline">
                                <Phone className="w-3 h-3" />
                                Call Shop
                              </a>
                            )}
                            <a
                              href={`https://www.google.com/maps/dir/?api=1&destination=${encodeURIComponent(currentOrder.restaurants?.address || '')}`}
                              target="_blank"
                              rel="noopener noreferrer"
                              className="flex items-center gap-1 text-accent text-sm hover:underline"
                            >
                              <ExternalLink className="w-3 h-3" />
                              Navigate
                            </a>
                          </div>
                        </div>
                      </div>
                      <div className="flex items-start gap-3 p-3 bg-card rounded-lg">
                        <Navigation className="w-5 h-5 text-primary mt-0.5" />
                        <div className="flex-1">
                          <p className="text-sm text-muted-foreground">Deliver to</p>
                          <p className="font-medium">{currentOrder.delivery_address}</p>
                          <div className="flex items-center gap-3 mt-2">
                            <a
                              href={`https://www.google.com/maps/dir/?api=1&destination=${encodeURIComponent(currentOrder.delivery_address)}`}
                              target="_blank"
                              rel="noopener noreferrer"
                              className="flex items-center gap-1 text-primary text-sm hover:underline"
                            >
                              <ExternalLink className="w-3 h-3" />
                              Navigate
                            </a>
                          </div>
                        </div>
                      </div>
                    </div>

                    <div className="space-y-3">
                      {currentOrder.status === 'on_the_way' && (
                        <div className="space-y-3">
                          <div className="p-3 bg-card rounded-lg border-2 border-primary/20">
                            <div className="flex items-center gap-2 mb-2">
                              <Key className="w-4 h-4 text-primary" />
                              <span className="text-sm font-medium">Enter Customer OTP</span>
                            </div>
                            <Input
                              type="text"
                              placeholder="Enter 4-digit OTP"
                              value={deliveryOtpInputs[currentOrder.id] || ''}
                              onChange={(e) => {
                                setDeliveryOtpInputs(prev => ({ ...prev, [currentOrder.id]: e.target.value.replace(/\D/g, '').slice(0, 4) }));
                                setDeliveryOtpErrors(prev => ({ ...prev, [currentOrder.id]: false }));
                              }}
                              className={`font-mono text-center text-lg tracking-widest ${deliveryOtpErrors[currentOrder.id] ? 'border-destructive' : ''}`}
                              maxLength={4}
                            />
                            {deliveryOtpErrors[currentOrder.id] && (
                              <div className="flex items-center gap-1 text-destructive text-sm mt-2">
                                <AlertCircle className="w-3 h-3" />
                                <span>Invalid OTP</span>
                              </div>
                            )}
                          </div>
                          <div className="flex gap-2">
                            <Button
                              className="flex-1"
                              variant="success"
                              onClick={() => verifyAndDeliver(currentOrder)}
                              disabled={(deliveryOtpInputs[currentOrder.id] || '').length !== 4}
                            >
                              <CheckCircle className="w-4 h-4 mr-2" />
                              Verify & Deliver
                            </Button>
                            <AlertDialog>
                              <AlertDialogTrigger asChild>
                                <Button variant="destructive" size="icon">
                                  <X className="w-4 h-4" />
                                </Button>
                              </AlertDialogTrigger>
                              <AlertDialogContent>
                                <AlertDialogHeader>
                                  <AlertDialogTitle>Release Order</AlertDialogTitle>
                                  <AlertDialogDescription>
                                    This order will be released back to the pool for another delivery partner to pick up.
                                  </AlertDialogDescription>
                                </AlertDialogHeader>
                                <AlertDialogFooter>
                                  <AlertDialogCancel>No, Keep Order</AlertDialogCancel>
                                  <AlertDialogAction
                                    onClick={() => releaseOrderToPool(currentOrder.id)}
                                    className="bg-destructive text-destructive-foreground hover:bg-destructive/90"
                                  >
                                    Yes, Release
                                  </AlertDialogAction>
                                </AlertDialogFooter>
                              </AlertDialogContent>
                            </AlertDialog>
                          </div>
                        </div>
                      )}
                      {currentOrder.status === 'picked_up' && (
                        <div className="flex gap-2">
                          <Button className="flex-1" onClick={() => updateOrderStatus(currentOrder.id, 'on_the_way')}>
                            <Navigation className="w-4 h-4 mr-2" />
                            Start Delivery
                          </Button>
                          <AlertDialog>
                            <AlertDialogTrigger asChild>
                              <Button variant="destructive" size="icon">
                                <X className="w-4 h-4" />
                              </Button>
                            </AlertDialogTrigger>
                            <AlertDialogContent>
                              <AlertDialogHeader>
                                <AlertDialogTitle>Release Order</AlertDialogTitle>
                                <AlertDialogDescription>
                                  This order will be released back to the pool for another delivery partner to pick up.
                                </AlertDialogDescription>
                              </AlertDialogHeader>
                              <AlertDialogFooter>
                                <AlertDialogCancel>No, Keep Order</AlertDialogCancel>
                                <AlertDialogAction
                                  onClick={() => releaseOrderToPool(currentOrder.id)}
                                  className="bg-destructive text-destructive-foreground hover:bg-destructive/90"
                                >
                                  Yes, Release
                                </AlertDialogAction>
                              </AlertDialogFooter>
                            </AlertDialogContent>
                          </AlertDialog>
                        </div>
                      )}
                    </div>
                  </div>
                </CardContent>
              </Card>
            )}

            {/* Available Orders */}
            {deliveryPartner?.is_available && !currentOrder && (
              <div className="space-y-4">
                <h2 className="text-lg font-semibold flex items-center gap-2">
                  <Package className="w-5 h-5" />
                  Available Orders
                  {availableOrders.length > 0 && (
                    <span className="w-6 h-6 bg-primary text-primary-foreground text-sm rounded-full flex items-center justify-center">
                      {availableOrders.length}
                    </span>
                  )}
                </h2>

                {availableOrders.length === 0 ? (
                  <Card className="border-0 shadow-sm">
                    <CardContent className="py-12 text-center">
                      <Package className="w-12 h-12 text-muted-foreground/30 mx-auto mb-4" />
                      <p className="text-muted-foreground">No orders available right now</p>
                      <p className="text-sm text-muted-foreground mt-1">New orders will appear here automatically</p>
                    </CardContent>
                  </Card>
                ) : (
                  <div className="grid gap-4">
                    {availableOrders.map((order) => (
                      <Card key={order.id} className="border-0 shadow-sm hover:shadow-md transition-shadow">
                        <CardContent className="p-4">
                          <div className="flex items-start justify-between mb-3">
                            <div>
                              <div className="flex items-center gap-2">
                                <p className="font-semibold">{order.restaurants?.name}</p>
                                <span className="text-xs font-mono bg-secondary px-1.5 py-0.5 rounded">
                                  {getShortOrderId(order.id)}
                                </span>
                              </div>
                              <div className="flex items-center gap-2 text-sm text-muted-foreground mt-1">
                                <Clock className="w-4 h-4" />
                                {format(new Date(order.created_at), 'h:mm a')}
                              </div>
                            </div>
                            <div className="text-right">
                              <p className="font-bold text-primary">{Number(order.total_amount).toFixed(2)}</p>
                              <span className="flex items-center gap-1 text-xs mt-1 justify-end">
                                {order.payment_method === 'cod' ? (
                                  <span className="flex items-center gap-1 bg-accent/10 text-accent px-2 py-0.5 rounded-full">
                                    <Banknote className="w-3 h-3" /> COD
                                  </span>
                                ) : (
                                  <span className="flex items-center gap-1 bg-primary/10 text-primary px-2 py-0.5 rounded-full">
                                    <Smartphone className="w-3 h-3" /> GPay
                                  </span>
                                )}
                              </span>
                            </div>
                          </div>

                          {/* Customer Info */}
                          <div className="flex items-center justify-between p-2 bg-secondary/50 rounded-lg mb-3">
                            <div className="flex items-center gap-2">
                              <User className="w-4 h-4 text-muted-foreground" />
                              <span className="text-sm">{order.customer_profile?.full_name || 'Customer'}</span>
                            </div>
                            {order.customer_profile?.phone && (
                              <a
                                href={`tel:${order.customer_profile.phone}`}
                                className="flex items-center gap-1 text-primary text-xs hover:underline"
                              >
                                <Phone className="w-3 h-3" />
                                Call
                              </a>
                            )}
                          </div>

                          <div className="space-y-2 text-sm mb-4">
                            <div className="flex items-start gap-2">
                              <MapPin className="w-4 h-4 text-accent mt-0.5" />
                              <div className="flex-1">
                                <span className="text-muted-foreground">{order.restaurants?.address}</span>
                                <div className="flex items-center gap-3 mt-1">
                                  {order.restaurants?.phone && (
                                    <a href={`tel:${order.restaurants.phone}`} className="flex items-center gap-1 text-primary text-xs hover:underline">
                                      <Phone className="w-3 h-3" />
                                      Call Shop
                                    </a>
                                  )}
                                  <a
                                    href={`https://www.google.com/maps/dir/?api=1&destination=${encodeURIComponent(order.restaurants?.address || '')}`}
                                    target="_blank"
                                    rel="noopener noreferrer"
                                    className="flex items-center gap-1 text-accent text-xs hover:underline"
                                  >
                                    <ExternalLink className="w-3 h-3" />
                                    Map
                                  </a>
                                </div>
                              </div>
                            </div>
                            <div className="flex items-start gap-2">
                              <Navigation className="w-4 h-4 text-primary mt-0.5" />
                              <span className="text-muted-foreground">{order.delivery_address}</span>
                            </div>
                          </div>

                          {/* Pickup OTP verification */}
                          <div className="space-y-3">
                            <div className="p-3 bg-card rounded-lg border">
                              <div className="flex items-center gap-2 mb-2">
                                <Key className="w-4 h-4 text-accent" />
                                <span className="text-sm font-medium">Enter Pickup OTP from Shop</span>
                              </div>
                              <Input
                                type="text"
                                placeholder="Enter 4-digit OTP"
                                value={pickupOtpInputs[order.id] || ''}
                                onChange={(e) => {
                                  setPickupOtpInputs(prev => ({ ...prev, [order.id]: e.target.value.replace(/\D/g, '').slice(0, 4) }));
                                  setPickupOtpErrors(prev => ({ ...prev, [order.id]: false }));
                                }}
                                className={`font-mono text-center text-lg tracking-widest ${pickupOtpErrors[order.id] ? 'border-destructive' : ''}`}
                                maxLength={4}
                              />
                              {pickupOtpErrors[order.id] && (
                                <div className="flex items-center gap-1 text-destructive text-sm mt-2">
                                  <AlertCircle className="w-3 h-3" />
                                  <span>Invalid OTP</span>
                                </div>
                              )}
                            </div>
                            <Button
                              className="w-full"
                              onClick={() => verifyPickupAndAccept(order)}
                              disabled={(pickupOtpInputs[order.id] || '').length !== 4}
                            >
                              Verify & Accept Order
                            </Button>
                          </div>
                        </CardContent>
                      </Card>
                    ))}
                  </div>
                )}
              </div>
            )}

            {/* Offline State */}
            {!deliveryPartner?.is_available && !currentOrder && (
              <Card className="border-0 shadow-sm">
                <CardContent className="py-16 text-center">
                  <Bike className="w-16 h-16 text-muted-foreground/30 mx-auto mb-4" />
                  <h3 className="text-xl font-semibold mb-2">You're Offline</h3>
                  <p className="text-muted-foreground mb-4">Go online to start receiving delivery requests</p>
                  <Button onClick={toggleAvailability}>Go Online</Button>
                </CardContent>
              </Card>
            )}
          </TabsContent>

          <TabsContent value="history" className="space-y-4">
            <h2 className="text-lg font-semibold flex items-center gap-2">
              <History className="w-5 h-5" />
              Order History
            </h2>

            {orderHistory.length === 0 ? (
              <Card className="border-0 shadow-sm">
                <CardContent className="py-12 text-center">
                  <History className="w-12 h-12 text-muted-foreground/30 mx-auto mb-4" />
                  <p className="text-muted-foreground">No completed deliveries yet</p>
                  <p className="text-sm text-muted-foreground mt-1">Your delivery history will appear here</p>
                </CardContent>
              </Card>
            ) : (
              <div className="grid gap-4">
                {orderHistory.map((order) => (
                  <Card key={order.id} className="border-0 shadow-sm">
                    <CardContent className="p-4">
                      <div className="flex items-start justify-between mb-2">
                        <div>
                          <div className="flex items-center gap-2">
                            <p className="font-semibold">{order.restaurants?.name}</p>
                            <span className="text-xs font-mono bg-secondary px-1.5 py-0.5 rounded">
                              {getShortOrderId(order.id)}
                            </span>
                          </div>
                          <div className="flex items-center gap-2 text-sm text-muted-foreground mt-1">
                            <Clock className="w-4 h-4" />
                            {format(new Date(order.created_at), 'MMM d, h:mm a')}
                          </div>
                        </div>
                        <div className="text-right">
                          <StatusBadge status={order.status as OrderStatus} />
                          <p className="font-bold text-primary mt-1">{Number(order.total_amount).toFixed(2)}</p>
                        </div>
                      </div>
                      <div className="flex items-center gap-2 text-sm text-muted-foreground">
                        <Navigation className="w-4 h-4" />
                        <span className="truncate">{order.delivery_address}</span>
                      </div>
                      {order.status === 'delivered' && (
                        <div className="mt-2 flex items-center gap-1 text-accent text-sm">
                          <IndianRupee className="w-3 h-3" />
                          <span>Earned: 30</span>
                        </div>
                      )}
                    </CardContent>
                  </Card>
                ))}
              </div>
            )}
          </TabsContent>

          <TabsContent value="earnings" className="space-y-6">
            {/* Earnings Stats */}
            <div className="grid grid-cols-2 gap-4">
              <Card className="border-0 shadow-sm">
                <CardContent className="p-4">
                  <div className="flex items-center gap-3">
                    <div className="w-10 h-10 rounded-lg bg-accent/10 flex items-center justify-center">
                      <IndianRupee className="w-5 h-5 text-accent" />
                    </div>
                    <div>
                      <p className="text-sm text-muted-foreground">Today</p>
                      <p className="text-xl font-bold">{earnings.today}</p>
                    </div>
                  </div>
                </CardContent>
              </Card>
              <Card className="border-0 shadow-sm">
                <CardContent className="p-4">
                  <div className="flex items-center gap-3">
                    <div className="w-10 h-10 rounded-lg bg-primary/10 flex items-center justify-center">
                      <TrendingUp className="w-5 h-5 text-primary" />
                    </div>
                    <div>
                      <p className="text-sm text-muted-foreground">This Week</p>
                      <p className="text-xl font-bold">{earnings.week}</p>
                    </div>
                  </div>
                </CardContent>
              </Card>
              <Card className="border-0 shadow-sm">
                <CardContent className="p-4">
                  <div className="flex items-center gap-3">
                    <div className="w-10 h-10 rounded-lg bg-status-delivered/10 flex items-center justify-center">
                      <Wallet className="w-5 h-5 text-status-delivered" />
                    </div>
                    <div>
                      <p className="text-sm text-muted-foreground">Total Earned</p>
                      <p className="text-xl font-bold">{earnings.total}</p>
                    </div>
                  </div>
                </CardContent>
              </Card>
              <Card className="border-0 shadow-sm">
                <CardContent className="p-4">
                  <div className="flex items-center gap-3">
                    <div className="w-10 h-10 rounded-lg bg-status-pending/10 flex items-center justify-center">
                      <Clock className="w-5 h-5 text-status-pending" />
                    </div>
                    <div>
                      <p className="text-sm text-muted-foreground">Pending</p>
                      <p className="text-xl font-bold">{earnings.pending}</p>
                    </div>
                  </div>
                </CardContent>
              </Card>
            </div>

            {/* Settlement Info */}
            <Card className="border-0 shadow-md bg-gradient-to-r from-primary/5 to-accent/5">
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <Wallet className="w-5 h-5 text-primary" />
                  Settlement Info
                </CardTitle>
              </CardHeader>
              <CardContent>
                <div className="space-y-4">
                  <div className="flex justify-between items-center p-3 bg-card rounded-lg">
                    <span className="text-muted-foreground">Delivery fee per order</span>
                    <span className="font-bold">30</span>
                  </div>
                  <div className="flex justify-between items-center p-3 bg-card rounded-lg">
                    <span className="text-muted-foreground">Total deliveries</span>
                    <span className="font-bold">{orderHistory.filter(o => o.status === 'delivered').length}</span>
                  </div>
                  <div className="flex justify-between items-center p-3 bg-card rounded-lg">
                    <span className="text-muted-foreground">Settlement cycle</span>
                    <span className="font-bold">Weekly (Every Monday)</span>
                  </div>
                  <div className="flex justify-between items-center p-3 bg-card rounded-lg">
                    <span className="text-muted-foreground">Settlement status</span>
                    <span className="font-bold text-accent">Processed</span>
                  </div>
                  <div className="flex justify-between items-center p-3 bg-card rounded-lg">
                    <span className="text-muted-foreground">Next payout</span>
                    <span className="font-bold">{earnings.pending}</span>
                  </div>

                  {/* Payment breakdown */}
                  <div className="mt-4 p-4 bg-secondary/50 rounded-lg">
                    <h4 className="font-semibold mb-3 flex items-center gap-2">
                      <FileText className="w-4 h-4 text-primary" />
                      Earnings Breakdown
                    </h4>
                    <div className="space-y-2 text-sm">
                      <div className="flex justify-between">
                        <span className="text-muted-foreground">Gross earnings</span>
                        <span>{earnings.total}</span>
                      </div>
                      <div className="flex justify-between">
                        <span className="text-muted-foreground">Platform fee (0%)</span>
                        <span>0</span>
                      </div>
                      <div className="flex justify-between">
                        <span className="text-muted-foreground">Incentives & bonuses</span>
                        <span className="text-accent">+{Math.floor(orderHistory.filter(o => o.status === 'delivered').length * 5)}</span>
                      </div>
                      <div className="border-t border-border my-2" />
                      <div className="flex justify-between font-bold">
                        <span>Net payout</span>
                        <span className="text-primary">{earnings.total + Math.floor(orderHistory.filter(o => o.status === 'delivered').length * 5)}</span>
                      </div>
                    </div>
                  </div>

                  <p className="text-sm text-muted-foreground text-center mt-4">
                    Settlements are processed every Monday to your registered bank account
                  </p>
                </div>
              </CardContent>
            </Card>
          </TabsContent>

          <TabsContent value="profile" className="space-y-6">
            {/* Profile Info */}
            <Card className="border-0 shadow-md">
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <User className="w-5 h-5 text-primary" />
                  Partner Profile
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="flex justify-between items-center p-3 bg-secondary/50 rounded-lg">
                  <span className="text-muted-foreground">Name</span>
                  <span className="font-medium">{profile?.full_name || 'Not set'}</span>
                </div>
                <div className="flex justify-between items-center p-3 bg-secondary/50 rounded-lg">
                  <span className="text-muted-foreground">Phone</span>
                  <span className="font-medium">{deliveryPartner?.phone || 'Not set'}</span>
                </div>
                <div className="flex justify-between items-center p-3 bg-secondary/50 rounded-lg">
                  <span className="text-muted-foreground">Vehicle Type</span>
                  <span className="font-medium flex items-center gap-2">
                    <Bike className="w-4 h-4" />
                    {deliveryPartner?.vehicle_type || 'Bike'}
                  </span>
                </div>
                <div className="flex justify-between items-center p-3 bg-secondary/50 rounded-lg">
                  <span className="text-muted-foreground">Phone Verified</span>
                  <span className={`font-medium flex items-center gap-1 ${deliveryPartner?.phone_verified ? 'text-accent' : 'text-destructive'}`}>
                    {deliveryPartner?.phone_verified ? (
                      <>
                        <CheckCircle className="w-4 h-4" />
                        Verified
                      </>
                    ) : (
                      <>
                        <AlertCircle className="w-4 h-4" />
                        Not Verified
                      </>
                    )}
                  </span>
                </div>
                <div className="flex justify-between items-center p-3 bg-secondary/50 rounded-lg">
                  <span className="text-muted-foreground">Status</span>
                  <span className={`font-medium ${deliveryPartner?.is_available ? 'text-accent' : 'text-muted-foreground'}`}>
                    {deliveryPartner?.is_available ? 'Online' : 'Offline'}
                  </span>
                </div>
              </CardContent>
            </Card>

            {/* Performance Report */}
            <Card className="border-0 shadow-md">
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <BarChart3 className="w-5 h-5 text-primary" />
                  Performance Report
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="grid grid-cols-2 gap-3">
                  <div className="p-3 bg-secondary/50 rounded-lg text-center">
                    <div className="flex items-center justify-center gap-1 mb-1">
                      <Package className="w-4 h-4 text-primary" />
                      <span className="text-2xl font-bold">{orderHistory.filter(o => o.status === 'delivered').length}</span>
                    </div>
                    <p className="text-xs text-muted-foreground">Total Deliveries</p>
                  </div>
                  <div className="p-3 bg-secondary/50 rounded-lg text-center">
                    <div className="flex items-center justify-center gap-1 mb-1">
                      <Percent className="w-4 h-4 text-accent" />
                      <span className="text-2xl font-bold text-accent">
                        {orderHistory.length > 0
                          ? Math.round((orderHistory.filter(o => o.status === 'delivered').length / orderHistory.length) * 100)
                          : 0}%
                      </span>
                    </div>
                    <p className="text-xs text-muted-foreground">Success Rate</p>
                  </div>
                  <div className="p-3 bg-secondary/50 rounded-lg text-center">
                    <div className="flex items-center justify-center gap-1 mb-1">
                      <X className="w-4 h-4 text-destructive" />
                      <span className="text-2xl font-bold text-destructive">{orderHistory.filter(o => o.status === 'cancelled').length}</span>
                    </div>
                    <p className="text-xs text-muted-foreground">Cancelled</p>
                  </div>
                  <div className="p-3 bg-secondary/50 rounded-lg text-center">
                    <div className="flex items-center justify-center gap-1 mb-1">
                      <Star className="w-4 h-4 text-yellow-500 fill-yellow-500" />
                      <span className="text-2xl font-bold">
                        {avgRating !== null ? avgRating.toFixed(1) : ''}
                      </span>
                    </div>
                    <p className="text-xs text-muted-foreground">
                      Avg Rating {totalRatings > 0 ? `(${totalRatings})` : ''}
                    </p>
                  </div>
                </div>

                {/* Rating Progress */}
                <div className="p-3 bg-gradient-to-r from-primary/10 to-accent/10 rounded-lg">
                  <div className="flex items-center justify-between mb-2">
                    <span className="text-sm font-medium flex items-center gap-1">
                      <Award className="w-4 h-4 text-primary" />
                      Partner Level
                    </span>
                    <span className="text-sm text-primary font-bold">
                      {orderHistory.filter(o => o.status === 'delivered').length >= 100 ? 'Gold' :
                        orderHistory.filter(o => o.status === 'delivered').length >= 50 ? 'Silver' : 'Bronze'}
                    </span>
                  </div>
                  <div className="w-full bg-secondary rounded-full h-2">
                    <div
                      className="bg-gradient-to-r from-primary to-accent h-2 rounded-full transition-all"
                      style={{ width: `${Math.min(100, (orderHistory.filter(o => o.status === 'delivered').length / 100) * 100)}%` }}
                    />
                  </div>
                  <p className="text-xs text-muted-foreground mt-1">
                    {100 - orderHistory.filter(o => o.status === 'delivered').length} more deliveries to reach Gold
                  </p>
                </div>

                <div className="flex justify-between items-center p-3 bg-secondary/50 rounded-lg">
                  <span className="text-muted-foreground flex items-center gap-2">
                    <Calendar className="w-4 h-4" />
                    Member Since
                  </span>
                  <span className="font-medium">
                    {deliveryPartner?.created_at
                      ? format(new Date(deliveryPartner.created_at), 'MMM d, yyyy')
                      : 'N/A'}
                  </span>
                </div>
              </CardContent>
            </Card>

            {/* Help & Support */}
            <Card className="border-0 shadow-md">
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <Headphones className="w-5 h-5 text-primary" />
                  Help & Support
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-3">
                <Link to="/contact" className="flex items-center justify-between p-3 bg-secondary/50 rounded-lg hover:bg-secondary transition-colors">
                  <div className="flex items-center gap-3">
                    <Phone className="w-5 h-5 text-primary" />
                    <div>
                      <p className="font-medium">Contact Support</p>
                      <p className="text-xs text-muted-foreground">Get help with orders & account</p>
                    </div>
                  </div>
                  <ExternalLink className="w-4 h-4 text-muted-foreground" />
                </Link>
                <Link to="/terms" className="flex items-center justify-between p-3 bg-secondary/50 rounded-lg hover:bg-secondary transition-colors">
                  <div className="flex items-center gap-3">
                    <FileText className="w-5 h-5 text-primary" />
                    <div>
                      <p className="font-medium">Partner Guidelines</p>
                      <p className="text-xs text-muted-foreground">Read delivery partner terms</p>
                    </div>
                  </div>
                  <ExternalLink className="w-4 h-4 text-muted-foreground" />
                </Link>
                <a href="tel:+911800123456" className="flex items-center justify-between p-3 bg-primary/10 rounded-lg hover:bg-primary/20 transition-colors">
                  <div className="flex items-center gap-3">
                    <Headphones className="w-5 h-5 text-primary" />
                    <div>
                      <p className="font-medium text-primary">Emergency Helpline</p>
                      <p className="text-xs text-muted-foreground">+91 1800-123-4567</p>
                    </div>
                  </div>
                  <Phone className="w-4 h-4 text-primary" />
                </a>
              </CardContent>
            </Card>
          </TabsContent>
        </Tabs>
      </div>
    </div>
  );
}

import { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { supabase } from '@/integrations/supabase/client';
import { useAuth } from '@/hooks/useAuth';
import { Restaurant, MenuItem, Order, OrderStatus } from '@/types/database';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Input } from '@/components/ui/input';
import { Textarea } from '@/components/ui/textarea';
import { Label } from '@/components/ui/label';
import { Switch } from '@/components/ui/switch';
import { StatusBadge } from '@/components/StatusBadge';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from '@/components/ui/dialog';
import { MenuImageUpload } from '@/components/MenuImageUpload';
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
  AlertDialogTrigger,
} from "@/components/ui/alert-dialog";
import {
  Store,
  ArrowLeft,
  Plus,
  Package,
  Clock,
  Edit,
  Trash2,
  Key,
  Banknote,
  Smartphone,
  User,
  Phone,
  XCircle,
  History,
  TrendingUp,
  IndianRupee,
  Wallet,
  MapPin,
  Navigation,
  Loader2,
  Settings,
  RefreshCw,
  Search,
  Bike,
  Percent
} from 'lucide-react';
import { toast } from 'sonner';
import { format } from 'date-fns';
import { useRestaurantOrderNotifications } from '@/hooks/useOrderNotifications';

interface OrderWithItems extends Order {
  order_items: { quantity: number; unit_price: number; menu_items: { name: string } | null }[];
  customer_profile?: { full_name: string | null; phone: string | null } | null;
}

// Helper to generate short order ID
const getShortOrderId = (id: string) => {
  return `#${id.slice(0, 8).toUpperCase()}`;
};

export default function RestaurantDashboard() {
  const { user, loading: authLoading } = useAuth();
  const navigate = useNavigate();
  const [restaurant, setRestaurant] = useState<Restaurant | null>(null);
  const [menuItems, setMenuItems] = useState<MenuItem[]>([]);
  const [orders, setOrders] = useState<OrderWithItems[]>([]);
  const [orderHistory, setOrderHistory] = useState<OrderWithItems[]>([]);
  const [earnings, setEarnings] = useState({
    today: 0, week: 0, total: 0, orderCount: 0,
    grossToday: 0, grossWeek: 0, grossTotal: 0,
    deliveryFees: 0, platformFees: 0, commission: 0
  });
  const [loading, setLoading] = useState(true);
  const [refreshing, setRefreshing] = useState(false);
  const [showCreateRestaurant, setShowCreateRestaurant] = useState(false);
  const [showAddItem, setShowAddItem] = useState(false);
  const [editingItem, setEditingItem] = useState<MenuItem | null>(null);
  const [searchOrders, setSearchOrders] = useState('');
  const [searchMenu, setSearchMenu] = useState('');

  // Form states
  const [restaurantForm, setRestaurantForm] = useState({
    name: '',
    description: '',
    address: '',
    phone: '',
    cuisine_type: '',
    image_url: '',
    lat: '',
    lng: '',
  });
  const [detectingLocation, setDetectingLocation] = useState(false);

  const [menuItemForm, setMenuItemForm] = useState({
    name: '',
    description: '',
    price: '',
    category: '',
    image_url: '',
  });

  useEffect(() => {
    if (!authLoading) {
      if (!user) {
        navigate('/auth');
        return;
      }
      fetchData();
    }
  }, [user, authLoading]);

  useEffect(() => {
    if (restaurant?.id) {
      subscribeToOrders();
    }
  }, [restaurant?.id]);

  // Sound notifications for new orders
  useRestaurantOrderNotifications(restaurant?.id);

  const fetchData = async () => {
    // Fetch restaurant owned by user
    const { data: restaurantData } = await supabase
      .from('restaurants')
      .select('*')
      .eq('owner_id', user!.id)
      .maybeSingle();

    if (restaurantData) {
      setRestaurant(restaurantData);

      // Fetch menu items
      const { data: menuData } = await supabase
        .from('menu_items')
        .select('*')
        .eq('restaurant_id', restaurantData.id)
        .order('category', { ascending: true });

      if (menuData) setMenuItems(menuData);

      // Fetch active orders
      const { data: ordersData } = await supabase
        .from('orders')
        .select('*, order_items(quantity, unit_price, menu_items(name))')
        .eq('restaurant_id', restaurantData.id)
        .in('status', ['pending', 'confirmed', 'preparing', 'ready_for_pickup', 'picked_up', 'on_the_way'])
        .order('created_at', { ascending: false })
        .limit(50);

      // Fetch order history (delivered/cancelled)
      const { data: historyData } = await supabase
        .from('orders')
        .select('*, order_items(quantity, unit_price, menu_items(name))')
        .eq('restaurant_id', restaurantData.id)
        .in('status', ['delivered', 'cancelled'])
        .order('created_at', { ascending: false })
        .limit(50);

      const allOrders = [...(ordersData || []), ...(historyData || [])];
      const customerIds = allOrders.map(o => o.customer_id);
      const { data: customerProfiles } = await supabase
        .from('profiles')
        .select('id, full_name, phone')
        .in('id', customerIds);

      if (ordersData) {
        const ordersWithProfiles = ordersData.map(order => ({
          ...order,
          customer_profile: customerProfiles?.find(p => p.id === order.customer_id) || null
        }));
        setOrders(ordersWithProfiles as OrderWithItems[]);
      }

      if (historyData) {
        const historyWithProfiles = historyData.map(order => ({
          ...order,
          customer_profile: customerProfiles?.find(p => p.id === order.customer_id) || null
        }));
        setOrderHistory(historyWithProfiles as OrderWithItems[]);

        // Calculate earnings - EXCLUDE delivery fee and platform fee
        const deliveredOrders = historyData.filter(o => o.status === 'delivered');
        const today = new Date();
        today.setHours(0, 0, 0, 0);
        const weekAgo = new Date(today);
        weekAgo.setDate(weekAgo.getDate() - 7);

        const platformFee = 8; // Fixed platform fee per order
        const deliveryFee = 25; // Default delivery fee per order
        const commissionRate = restaurantData.commission_rate || 15;

        // Calculate per-order shop earnings (total - delivery - platform - commission)
        const calculateShopEarnings = (order: OrderWithItems) => {
          const orderDeliveryFee = Number(order.delivery_fee || deliveryFee);
          const subtotal = Number(order.total_amount) - orderDeliveryFee - platformFee;
          const commission = (subtotal * commissionRate) / 100;
          return Math.max(0, subtotal - commission);
        };

        const todayOrders = deliveredOrders.filter(o => new Date(o.created_at) >= today);
        const weekOrders = deliveredOrders.filter(o => new Date(o.created_at) >= weekAgo);

        const grossToday = todayOrders.reduce((sum, o) => sum + Number(o.total_amount), 0);
        const grossWeek = weekOrders.reduce((sum, o) => sum + Number(o.total_amount), 0);
        const grossTotal = deliveredOrders.reduce((sum, o) => sum + Number(o.total_amount), 0);

        const todayShopEarnings = todayOrders.reduce((sum, o) => sum + calculateShopEarnings(o), 0);
        const weekShopEarnings = weekOrders.reduce((sum, o) => sum + calculateShopEarnings(o), 0);
        const totalShopEarnings = deliveredOrders.reduce((sum, o) => sum + calculateShopEarnings(o), 0);

        const totalDeliveryFees = deliveredOrders.reduce((sum, o) => sum + Number(o.delivery_fee || deliveryFee), 0);
        const totalPlatformFees = deliveredOrders.length * platformFee;
        const totalCommission = deliveredOrders.reduce((sum, o) => {
          const orderDeliveryFee = Number(o.delivery_fee || deliveryFee);
          const subtotal = Number(o.total_amount) - orderDeliveryFee - platformFee;
          return sum + (subtotal * commissionRate) / 100;
        }, 0);

        setEarnings({
          today: todayShopEarnings,
          week: weekShopEarnings,
          total: totalShopEarnings,
          orderCount: deliveredOrders.length,
          grossToday,
          grossWeek,
          grossTotal,
          deliveryFees: totalDeliveryFees,
          platformFees: totalPlatformFees,
          commission: totalCommission
        });
      }
    } else {
      setShowCreateRestaurant(true);
    }

    setLoading(false);
    setRefreshing(false);
  };

  const handleRefresh = () => {
    setRefreshing(true);
    fetchData();
  };

  // Filtered data
  const filteredOrders = orders.filter(o =>
    o.id.toLowerCase().includes(searchOrders.toLowerCase()) ||
    (o.customer_profile?.full_name?.toLowerCase().includes(searchOrders.toLowerCase()))
  );

  const filteredMenuItems = menuItems.filter(item =>
    item.name.toLowerCase().includes(searchMenu.toLowerCase()) ||
    (item.category?.toLowerCase().includes(searchMenu.toLowerCase()))
  );

  const subscribeToOrders = () => {
    const channel = supabase
      .channel('restaurant-orders')
      .on(
        'postgres_changes',
        {
          event: '*',
          schema: 'public',
          table: 'orders',
        },
        () => {
          fetchData();
        }
      )
      .subscribe();

    return () => {
      supabase.removeChannel(channel);
    };
  };

  const createRestaurant = async () => {
    if (!restaurantForm.name || !restaurantForm.address) {
      toast.error('Name and address are required');
      return;
    }

    const { data, error } = await supabase
      .from('restaurants')
      .insert({
        owner_id: user!.id,
        name: restaurantForm.name,
        description: restaurantForm.description || null,
        address: restaurantForm.address,
        phone: restaurantForm.phone || null,
        cuisine_type: restaurantForm.cuisine_type || null,
        image_url: restaurantForm.image_url || null,
        lat: restaurantForm.lat ? parseFloat(restaurantForm.lat) : null,
        lng: restaurantForm.lng ? parseFloat(restaurantForm.lng) : null,
      })
      .select()
      .single();

    if (error) {
      toast.error(error.message);
    } else {
      // Use secure RPC to assign restaurant_owner role
      await supabase.rpc('request_restaurant_owner_role');

      setRestaurant(data);
      setShowCreateRestaurant(false);
      toast.success('Shop created! Awaiting admin verification.');
    }
  };

  const detectCurrentLocation = () => {
    if (!navigator.geolocation) {
      toast.error('Geolocation is not supported by your browser');
      return;
    }

    setDetectingLocation(true);
    navigator.geolocation.getCurrentPosition(
      (position) => {
        setRestaurantForm(prev => ({
          ...prev,
          lat: position.coords.latitude.toString(),
          lng: position.coords.longitude.toString(),
        }));
        setDetectingLocation(false);
        toast.success('Location detected!');
      },
      (error) => {
        setDetectingLocation(false);
        toast.error('Failed to get location: ' + error.message);
      },
      { enableHighAccuracy: true, timeout: 15000 }
    );
  };

  const updateShopLocation = async () => {
    if (!restaurant) return;

    const { error } = await supabase
      .from('restaurants')
      .update({
        lat: restaurantForm.lat ? parseFloat(restaurantForm.lat) : null,
        lng: restaurantForm.lng ? parseFloat(restaurantForm.lng) : null,
      })
      .eq('id', restaurant.id);

    if (error) {
      toast.error('Failed to update location');
    } else {
      setRestaurant(prev => prev ? {
        ...prev,
        lat: restaurantForm.lat ? parseFloat(restaurantForm.lat) : null,
        lng: restaurantForm.lng ? parseFloat(restaurantForm.lng) : null,
      } : null);
      toast.success('Shop location updated!');
    }
  };

  const toggleShopStatus = async () => {
    if (!restaurant) return;

    const { error } = await supabase
      .from('restaurants')
      .update({ is_open: !restaurant.is_open })
      .eq('id', restaurant.id);

    if (error) {
      toast.error('Failed to update status');
    } else {
      setRestaurant({ ...restaurant, is_open: !restaurant.is_open });
      toast.success(restaurant.is_open ? 'Shop closed' : 'Shop opened');
    }
  };

  const addMenuItem = async () => {
    if (!menuItemForm.name || !menuItemForm.price) {
      toast.error('Name and price are required');
      return;
    }

    const { error } = await supabase.from('menu_items').insert({
      restaurant_id: restaurant!.id,
      name: menuItemForm.name,
      description: menuItemForm.description || null,
      price: parseFloat(menuItemForm.price),
      category: menuItemForm.category || null,
      image_url: menuItemForm.image_url || null,
    });

    if (error) {
      toast.error(error.message);
    } else {
      setMenuItemForm({ name: '', description: '', price: '', category: '', image_url: '' });
      setShowAddItem(false);
      fetchData();
      toast.success('Menu item added');
    }
  };

  const updateMenuItem = async () => {
    if (!editingItem) return;

    const { error } = await supabase
      .from('menu_items')
      .update({
        name: menuItemForm.name,
        description: menuItemForm.description || null,
        price: parseFloat(menuItemForm.price),
        category: menuItemForm.category || null,
        image_url: menuItemForm.image_url || null,
      })
      .eq('id', editingItem.id);

    if (error) {
      toast.error(error.message);
    } else {
      setEditingItem(null);
      setMenuItemForm({ name: '', description: '', price: '', category: '', image_url: '' });
      fetchData();
      toast.success('Menu item updated');
    }
  };

  const deleteMenuItem = async (id: string) => {
    const { error } = await supabase.from('menu_items').delete().eq('id', id);
    if (error) {
      toast.error(error.message);
    } else {
      fetchData();
      toast.success('Menu item deleted');
    }
  };

  const toggleItemAvailability = async (item: MenuItem) => {
    const { error } = await supabase
      .from('menu_items')
      .update({ is_available: !item.is_available })
      .eq('id', item.id);

    if (!error) {
      fetchData();
    }
  };

  const updateOrderStatus = async (orderId: string, status: OrderStatus) => {
    const { error } = await supabase
      .from('orders')
      .update({ status })
      .eq('id', orderId);

    if (error) {
      toast.error('Failed to update order');
    } else {
      fetchData();
      toast.success('Order status updated');
    }
  };

  const cancelOrder = async (orderId: string) => {
    const { error } = await supabase
      .from('orders')
      .update({ status: 'cancelled' })
      .eq('id', orderId);

    if (error) {
      toast.error('Failed to cancel order');
    } else {
      fetchData();
      toast.success('Order cancelled');
    }
  };

  if (authLoading || loading) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center">
        <div className="animate-pulse text-muted-foreground">Loading...</div>
      </div>
    );
  }

  // Show create restaurant form
  if (showCreateRestaurant) {
    return (
      <div className="min-h-screen bg-background">
        <header className="sticky top-0 z-50 bg-card/95 backdrop-blur-md border-b border-border">
          <div className="container mx-auto px-4 py-4">
            <Link to="/" className="flex items-center gap-2 text-foreground hover:text-primary transition-colors">
              <ArrowLeft className="w-5 h-5" />
              <span>Back</span>
            </Link>
          </div>
        </header>
        <div className="container mx-auto px-4 py-8 max-w-xl">
          <Card className="border-0 shadow-lg">
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Store className="w-5 h-5" />
                Register Your Shop
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="space-y-2">
                <Label>Shop Name *</Label>
                <Input
                  value={restaurantForm.name}
                  onChange={(e) => setRestaurantForm({ ...restaurantForm, name: e.target.value })}
                  placeholder="My Shop"
                />
              </div>
              <div className="space-y-2">
                <Label>Description</Label>
                <Textarea
                  value={restaurantForm.description}
                  onChange={(e) => setRestaurantForm({ ...restaurantForm, description: e.target.value })}
                  placeholder="Tell customers about your shop..."
                />
              </div>
              <div className="space-y-2">
                <Label>Address *</Label>
                <Input
                  value={restaurantForm.address}
                  onChange={(e) => setRestaurantForm({ ...restaurantForm, address: e.target.value })}
                  placeholder="123 Main St, City"
                />
              </div>
              <div className="grid grid-cols-2 gap-4">
                <div className="space-y-2">
                  <Label>Phone</Label>
                  <Input
                    value={restaurantForm.phone}
                    onChange={(e) => setRestaurantForm({ ...restaurantForm, phone: e.target.value })}
                    placeholder="+91 1234567890"
                  />
                </div>
                <div className="space-y-2">
                  <Label>Category</Label>
                  <Input
                    value={restaurantForm.cuisine_type}
                    onChange={(e) => setRestaurantForm({ ...restaurantForm, cuisine_type: e.target.value })}
                    placeholder="Food, Grocery..."
                  />
                </div>
              </div>
              <div className="space-y-2">
                <Label>Image URL</Label>
                <Input
                  value={restaurantForm.image_url}
                  onChange={(e) => setRestaurantForm({ ...restaurantForm, image_url: e.target.value })}
                  placeholder="https://..."
                />
              </div>

              {/* Location Section */}
              <div className="space-y-3 p-3 bg-secondary/50 rounded-lg">
                <div className="flex items-center justify-between">
                  <Label className="flex items-center gap-2">
                    <MapPin className="w-4 h-4 text-primary" />
                    Shop Location
                  </Label>
                  <Button
                    type="button"
                    variant="outline"
                    size="sm"
                    onClick={detectCurrentLocation}
                    disabled={detectingLocation}
                    className="gap-1"
                  >
                    {detectingLocation ? (
                      <Loader2 className="w-3 h-3 animate-spin" />
                    ) : (
                      <Navigation className="w-3 h-3" />
                    )}
                    Detect
                  </Button>
                </div>
                <div className="grid grid-cols-2 gap-3">
                  <div className="space-y-1">
                    <Label className="text-xs text-muted-foreground">Latitude</Label>
                    <Input
                      type="number"
                      step="any"
                      value={restaurantForm.lat}
                      onChange={(e) => setRestaurantForm({ ...restaurantForm, lat: e.target.value })}
                      placeholder="12.9716"
                    />
                  </div>
                  <div className="space-y-1">
                    <Label className="text-xs text-muted-foreground">Longitude</Label>
                    <Input
                      type="number"
                      step="any"
                      value={restaurantForm.lng}
                      onChange={(e) => setRestaurantForm({ ...restaurantForm, lng: e.target.value })}
                      placeholder="77.5946"
                    />
                  </div>
                </div>
                {restaurantForm.lat && restaurantForm.lng && (
                  <p className="text-xs text-accent flex items-center gap-1">
                    <MapPin className="w-3 h-3" />
                    Location set successfully
                  </p>
                )}
                <p className="text-xs text-muted-foreground">
                  Location helps customers find your shop and calculate delivery distance
                </p>
              </div>

              <Button className="w-full" onClick={createRestaurant}>
                Register Shop
              </Button>
            </CardContent>
          </Card>
        </div>
      </div>
    );
  }

  const pendingOrders = orders.filter((o) => ['pending', 'confirmed', 'preparing', 'ready_for_pickup'].includes(o.status));

  return (
    <div className="min-h-screen bg-background">
      <header className="sticky top-0 z-50 bg-card/95 backdrop-blur-md border-b border-border">
        <div className="container mx-auto px-4 py-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-4">
              <Link to="/" className="text-foreground hover:text-primary transition-colors">
                <ArrowLeft className="w-5 h-5" />
              </Link>
              <div className="flex items-center gap-2">
                <Store className="w-5 h-5 text-primary hidden md:block" />
                <h1 className="text-xl font-bold">{restaurant?.name}</h1>
              </div>
            </div>
            <div className="flex items-center gap-3">
              <Button
                variant="ghost"
                size="icon"
                onClick={handleRefresh}
                disabled={refreshing}
                className="h-9 w-9"
              >
                <RefreshCw className={`w-4 h-4 ${refreshing ? 'animate-spin' : ''}`} />
              </Button>
              {!restaurant?.is_verified && (
                <span className="text-sm text-status-pending">Pending Verification</span>
              )}
              <div className="flex items-center gap-2">
                <span className={`text-sm ${restaurant?.is_open ? 'text-accent' : 'text-muted-foreground'}`}>
                  {restaurant?.is_open ? 'Open' : 'Closed'}
                </span>
                <Switch
                  checked={restaurant?.is_open || false}
                  onCheckedChange={toggleShopStatus}
                  disabled={!restaurant?.is_verified}
                />
              </div>
            </div>
          </div>
        </div>
      </header>

      <div className="container mx-auto px-4 py-6">
        <Tabs defaultValue="orders" className="space-y-6">
          <TabsList className="grid w-full grid-cols-5">
            <TabsTrigger value="orders" className="relative gap-2">
              <Package className="w-4 h-4" />
              <span className="hidden sm:inline">Orders</span>
              {pendingOrders.length > 0 && (
                <span className="w-5 h-5 bg-primary text-primary-foreground text-xs rounded-full flex items-center justify-center">
                  {pendingOrders.length}
                </span>
              )}
            </TabsTrigger>
            <TabsTrigger value="menu" className="gap-2">
              <Edit className="w-4 h-4" />
              <span className="hidden sm:inline">Menu</span>
            </TabsTrigger>
            <TabsTrigger value="history" className="gap-2">
              <History className="w-4 h-4" />
              <span className="hidden sm:inline">History</span>
            </TabsTrigger>
            <TabsTrigger value="earnings" className="gap-2">
              <Wallet className="w-4 h-4" />
              <span className="hidden sm:inline">Earnings</span>
            </TabsTrigger>
            <TabsTrigger value="settings" className="gap-2">
              <Settings className="w-4 h-4" />
              <span className="hidden sm:inline">Settings</span>
            </TabsTrigger>
          </TabsList>

          {/* Orders Tab */}
          <TabsContent value="orders" className="space-y-4">
            {/* Search */}
            <div className="relative">
              <Search className="absolute left-3 top-1/2 -translate-y-1/2 w-4 h-4 text-muted-foreground" />
              <Input
                placeholder="Search by order ID or customer..."
                value={searchOrders}
                onChange={(e) => setSearchOrders(e.target.value)}
                className="pl-9"
              />
            </div>

            {filteredOrders.length === 0 ? (
              <Card className="border-0 shadow-sm">
                <CardContent className="py-16 text-center">
                  <Package className="w-16 h-16 text-muted-foreground/30 mx-auto mb-4" />
                  <h3 className="text-xl font-semibold mb-2">
                    {searchOrders ? 'No orders found' : 'No active orders'}
                  </h3>
                  <p className="text-muted-foreground">
                    {searchOrders ? 'Try a different search term' : 'Orders will appear here when customers place them'}
                  </p>
                </CardContent>
              </Card>
            ) : (
              <div className="grid gap-4">
                {filteredOrders.map((order) => (
                  <Card key={order.id} className="border-0 shadow-sm">
                    <CardContent className="p-4">
                      <div className="flex items-start justify-between mb-3">
                        <div>
                          <div className="flex items-center gap-2">
                            <span className="font-mono text-sm bg-secondary px-1.5 py-0.5 rounded">
                              {getShortOrderId(order.id)}
                            </span>
                            <StatusBadge status={order.status as OrderStatus} />
                          </div>
                          <div className="flex items-center gap-2 text-sm text-muted-foreground mt-1">
                            <Clock className="w-4 h-4" />
                            {format(new Date(order.created_at), 'MMM d, h:mm a')}
                          </div>
                        </div>
                        <p className="font-bold text-primary text-lg">{Number(order.total_amount).toFixed(2)}</p>
                      </div>

                      {/* Customer Info */}
                      <div className="flex items-center justify-between p-2 bg-secondary/50 rounded-lg mb-3">
                        <div className="flex items-center gap-2">
                          <User className="w-4 h-4 text-muted-foreground" />
                          <span className="text-sm font-medium">{order.customer_profile?.full_name || 'Customer'}</span>
                        </div>
                        {order.customer_profile?.phone && (
                          <a
                            href={`tel:${order.customer_profile.phone}`}
                            className="flex items-center gap-1 bg-primary text-primary-foreground px-3 py-1 rounded-lg text-sm font-medium hover:bg-primary/90 transition-colors"
                          >
                            <Phone className="w-3 h-3" />
                            Call
                          </a>
                        )}
                      </div>

                      {/* Delivery Address */}
                      {order.delivery_address && (
                        <p className="text-sm text-muted-foreground mb-3 truncate">
                           {order.delivery_address}
                        </p>
                      )}

                      <div className="space-y-1 mb-3">
                        {order.order_items.map((item, idx) => (
                          <p key={idx} className="text-sm">
                            {item.quantity}x {item.menu_items?.name || 'Unknown'} - {(Number(item.unit_price) * item.quantity).toFixed(2)}
                          </p>
                        ))}
                      </div>

                      <div className="flex items-center justify-between pt-3 border-t border-border">
                        <div className="flex gap-2 flex-wrap">
                          {order.status === 'pending' && (
                            <>
                              <Button size="sm" onClick={() => updateOrderStatus(order.id, 'confirmed')}>
                                Accept
                              </Button>
                              <Button size="sm" variant="outline" onClick={() => updateOrderStatus(order.id, 'cancelled')}>
                                Reject
                              </Button>
                            </>
                          )}
                          {order.status === 'confirmed' && (
                            <>
                              <Button size="sm" onClick={() => updateOrderStatus(order.id, 'preparing')}>
                                Start Preparing
                              </Button>
                              <AlertDialog>
                                <AlertDialogTrigger asChild>
                                  <Button size="sm" variant="outline" className="text-destructive">
                                    <XCircle className="w-3 h-3 mr-1" />
                                    Cancel
                                  </Button>
                                </AlertDialogTrigger>
                                <AlertDialogContent>
                                  <AlertDialogHeader>
                                    <AlertDialogTitle>Cancel Order?</AlertDialogTitle>
                                    <AlertDialogDescription>
                                      Are you sure you want to cancel this order? The customer will be notified.
                                    </AlertDialogDescription>
                                  </AlertDialogHeader>
                                  <AlertDialogFooter>
                                    <AlertDialogCancel>Keep Order</AlertDialogCancel>
                                    <AlertDialogAction
                                      onClick={() => cancelOrder(order.id)}
                                      className="bg-destructive text-destructive-foreground hover:bg-destructive/90"
                                    >
                                      Yes, Cancel
                                    </AlertDialogAction>
                                  </AlertDialogFooter>
                                </AlertDialogContent>
                              </AlertDialog>
                            </>
                          )}
                          {order.status === 'preparing' && (
                            <>
                              <Button size="sm" onClick={() => updateOrderStatus(order.id, 'ready_for_pickup')}>
                                Ready for Pickup
                              </Button>
                              <AlertDialog>
                                <AlertDialogTrigger asChild>
                                  <Button size="sm" variant="outline" className="text-destructive">
                                    <XCircle className="w-3 h-3 mr-1" />
                                    Cancel
                                  </Button>
                                </AlertDialogTrigger>
                                <AlertDialogContent>
                                  <AlertDialogHeader>
                                    <AlertDialogTitle>Cancel Order?</AlertDialogTitle>
                                    <AlertDialogDescription>
                                      This order is being prepared. Are you sure you want to cancel it?
                                    </AlertDialogDescription>
                                  </AlertDialogHeader>
                                  <AlertDialogFooter>
                                    <AlertDialogCancel>Keep Order</AlertDialogCancel>
                                    <AlertDialogAction
                                      onClick={() => cancelOrder(order.id)}
                                      className="bg-destructive text-destructive-foreground hover:bg-destructive/90"
                                    >
                                      Yes, Cancel
                                    </AlertDialogAction>
                                  </AlertDialogFooter>
                                </AlertDialogContent>
                              </AlertDialog>
                            </>
                          )}
                        </div>
                      </div>

                      {/* Show Pickup OTP when ready for pickup */}
                      {order.status === 'ready_for_pickup' && order.pickup_otp && (
                        <div className="mt-3 p-3 bg-accent/10 rounded-lg border border-accent/20">
                          <div className="flex items-center gap-2">
                            <Key className="w-4 h-4 text-accent" />
                            <span className="text-sm font-medium text-accent">Pickup OTP:</span>
                            <span className="font-mono font-bold text-lg text-accent tracking-widest">{order.pickup_otp}</span>
                          </div>
                          <span className="text-sm text-muted-foreground">Share this code with the delivery partner for order handoff</span>
                        </div>
                      )}

                      {/* Payment method badge */}
                      <div className="flex items-center gap-2 mt-2">
                        {order.payment_method === 'cod' ? (
                          <span className="flex items-center gap-1 text-xs bg-accent/10 text-accent px-2 py-1 rounded-full">
                            <Banknote className="w-3 h-3" /> Cash on Delivery
                          </span>
                        ) : (
                          <span className="flex items-center gap-1 text-xs bg-primary/10 text-primary px-2 py-1 rounded-full">
                            <Smartphone className="w-3 h-3" /> Google Pay
                          </span>
                        )}
                      </div>
                    </CardContent>
                  </Card>
                ))}
              </div>
            )}
          </TabsContent>

          {/* Menu Tab */}
          <TabsContent value="menu" className="space-y-4">
            <div className="flex justify-between items-center">
              <h2 className="text-lg font-semibold">Menu Items</h2>
              <Dialog open={showAddItem} onOpenChange={setShowAddItem}>
                <DialogTrigger asChild>
                  <Button size="sm">
                    <Plus className="w-4 h-4 mr-2" />
                    Add Item
                  </Button>
                </DialogTrigger>
                <DialogContent>
                  <DialogHeader>
                    <DialogTitle>Add Menu Item</DialogTitle>
                  </DialogHeader>
                  <div className="space-y-4">
                    <div className="space-y-2">
                      <Label>Name *</Label>
                      <Input
                        value={menuItemForm.name}
                        onChange={(e) => setMenuItemForm({ ...menuItemForm, name: e.target.value })}
                      />
                    </div>
                    <div className="space-y-2">
                      <Label>Description</Label>
                      <Textarea
                        value={menuItemForm.description}
                        onChange={(e) => setMenuItemForm({ ...menuItemForm, description: e.target.value })}
                      />
                    </div>
                    <div className="grid grid-cols-2 gap-4">
                      <div className="space-y-2">
                        <Label>Price () *</Label>
                        <Input
                          type="number"
                          step="0.01"
                          value={menuItemForm.price}
                          onChange={(e) => setMenuItemForm({ ...menuItemForm, price: e.target.value })}
                        />
                      </div>
                      <div className="space-y-2">
                        <Label>Category</Label>
                        <Input
                          value={menuItemForm.category}
                          onChange={(e) => setMenuItemForm({ ...menuItemForm, category: e.target.value })}
                        />
                      </div>
                    </div>
                    <MenuImageUpload
                      currentImageUrl={menuItemForm.image_url}
                      onImageChange={(url) => setMenuItemForm({ ...menuItemForm, image_url: url })}
                      restaurantId={restaurant?.id || ''}
                    />
                    <Button className="w-full" onClick={addMenuItem}>Add Item</Button>
                  </div>
                </DialogContent>
              </Dialog>
            </div>

            {/* Search Menu */}
            <div className="relative">
              <Search className="absolute left-3 top-1/2 -translate-y-1/2 w-4 h-4 text-muted-foreground" />
              <Input
                placeholder="Search menu items..."
                value={searchMenu}
                onChange={(e) => setSearchMenu(e.target.value)}
                className="pl-9"
              />
            </div>

            {filteredMenuItems.length === 0 ? (
              <Card className="border-0 shadow-sm">
                <CardContent className="py-16 text-center">
                  <p className="text-muted-foreground">
                    {searchMenu ? 'No items found' : 'No menu items yet. Add your first item!'}
                  </p>
                </CardContent>
              </Card>
            ) : (
              <div className="grid gap-4">
                {filteredMenuItems.map((item) => (
                  <Card key={item.id} className="border-0 shadow-sm">
                    <CardContent className="p-4">
                      <div className="flex items-center gap-4">
                        {item.image_url && (
                          <img src={item.image_url} alt={item.name} className="w-16 h-16 object-cover rounded-lg" />
                        )}
                        <div className="flex-1">
                          <div className="flex items-center gap-2">
                            <h3 className="font-semibold">{item.name}</h3>
                            {!item.is_available && (
                              <span className="text-xs bg-muted text-muted-foreground px-2 py-0.5 rounded">Unavailable</span>
                            )}
                          </div>
                          {item.category && <p className="text-sm text-muted-foreground">{item.category}</p>}
                          <p className="text-primary font-bold">{Number(item.price).toFixed(2)}</p>
                        </div>
                        <div className="flex items-center gap-2">
                          <Switch
                            checked={item.is_available}
                            onCheckedChange={() => toggleItemAvailability(item)}
                          />
                          <Button
                            size="icon"
                            variant="ghost"
                            onClick={() => {
                              setEditingItem(item);
                              setMenuItemForm({
                                name: item.name,
                                description: item.description || '',
                                price: item.price.toString(),
                                category: item.category || '',
                                image_url: item.image_url || '',
                              });
                            }}
                          >
                            <Edit className="w-4 h-4" />
                          </Button>
                          <Button
                            size="icon"
                            variant="ghost"
                            className="text-destructive hover:text-destructive"
                            onClick={() => deleteMenuItem(item.id)}
                          >
                            <Trash2 className="w-4 h-4" />
                          </Button>
                        </div>
                      </div>
                    </CardContent>
                  </Card>
                ))}
              </div>
            )}

            {/* Edit Dialog */}
            <Dialog open={!!editingItem} onOpenChange={(open) => !open && setEditingItem(null)}>
              <DialogContent>
                <DialogHeader>
                  <DialogTitle>Edit Menu Item</DialogTitle>
                </DialogHeader>
                <div className="space-y-4">
                  <div className="space-y-2">
                    <Label>Name *</Label>
                    <Input
                      value={menuItemForm.name}
                      onChange={(e) => setMenuItemForm({ ...menuItemForm, name: e.target.value })}
                    />
                  </div>
                  <div className="space-y-2">
                    <Label>Description</Label>
                    <Textarea
                      value={menuItemForm.description}
                      onChange={(e) => setMenuItemForm({ ...menuItemForm, description: e.target.value })}
                    />
                  </div>
                  <div className="grid grid-cols-2 gap-4">
                    <div className="space-y-2">
                      <Label>Price () *</Label>
                      <Input
                        type="number"
                        step="0.01"
                        value={menuItemForm.price}
                        onChange={(e) => setMenuItemForm({ ...menuItemForm, price: e.target.value })}
                      />
                    </div>
                    <div className="space-y-2">
                      <Label>Category</Label>
                      <Input
                        value={menuItemForm.category}
                        onChange={(e) => setMenuItemForm({ ...menuItemForm, category: e.target.value })}
                      />
                    </div>
                  </div>
                  <MenuImageUpload
                    currentImageUrl={menuItemForm.image_url}
                    onImageChange={(url) => setMenuItemForm({ ...menuItemForm, image_url: url })}
                    restaurantId={restaurant?.id || ''}
                  />
                  <Button className="w-full" onClick={updateMenuItem}>Save Changes</Button>
                </div>
              </DialogContent>
            </Dialog>
          </TabsContent>

          {/* History Tab */}
          <TabsContent value="history" className="space-y-4">
            <h2 className="text-lg font-semibold flex items-center gap-2">
              <History className="w-5 h-5" />
              Order History
            </h2>
            {orderHistory.length === 0 ? (
              <Card className="border-0 shadow-sm">
                <CardContent className="py-16 text-center">
                  <History className="w-16 h-16 text-muted-foreground/30 mx-auto mb-4" />
                  <h3 className="text-xl font-semibold mb-2">No order history</h3>
                  <p className="text-muted-foreground">Completed orders will appear here</p>
                </CardContent>
              </Card>
            ) : (
              <div className="grid gap-3">
                {orderHistory.map((order) => (
                  <Card key={order.id} className="border-0 shadow-sm">
                    <CardContent className="p-4">
                      <div className="flex items-center justify-between">
                        <div className="flex items-center gap-3">
                          <span className="font-mono text-sm bg-secondary px-1.5 py-0.5 rounded">
                            {getShortOrderId(order.id)}
                          </span>
                          <StatusBadge status={order.status as OrderStatus} />
                        </div>
                        <p className="font-bold text-primary">{Number(order.total_amount).toFixed(2)}</p>
                      </div>
                      <div className="flex items-center justify-between mt-2 text-sm text-muted-foreground">
                        <span>{order.customer_profile?.full_name || 'Customer'}</span>
                        <span>{format(new Date(order.created_at), 'MMM d, h:mm a')}</span>
                      </div>
                    </CardContent>
                  </Card>
                ))}
              </div>
            )}
          </TabsContent>

          {/* Earnings Tab */}
          <TabsContent value="earnings" className="space-y-6">
            {/* Net Earnings Cards */}
            <div>
              <h3 className="text-sm font-medium text-muted-foreground mb-3">Your Net Earnings</h3>
              <div className="grid grid-cols-2 gap-4">
                <Card className="border-0 shadow-sm bg-gradient-to-br from-accent/5 to-accent/10">
                  <CardContent className="p-4">
                    <div className="flex items-center gap-3">
                      <div className="w-10 h-10 rounded-lg bg-accent/20 flex items-center justify-center">
                        <IndianRupee className="w-5 h-5 text-accent" />
                      </div>
                      <div>
                        <p className="text-sm text-muted-foreground">Today</p>
                        <p className="text-2xl font-bold text-accent">{earnings.today.toFixed(0)}</p>
                      </div>
                    </div>
                  </CardContent>
                </Card>
                <Card className="border-0 shadow-sm bg-gradient-to-br from-primary/5 to-primary/10">
                  <CardContent className="p-4">
                    <div className="flex items-center gap-3">
                      <div className="w-10 h-10 rounded-lg bg-primary/20 flex items-center justify-center">
                        <TrendingUp className="w-5 h-5 text-primary" />
                      </div>
                      <div>
                        <p className="text-sm text-muted-foreground">This Week</p>
                        <p className="text-2xl font-bold text-primary">{earnings.week.toFixed(0)}</p>
                      </div>
                    </div>
                  </CardContent>
                </Card>
                <Card className="border-0 shadow-sm bg-gradient-to-br from-status-delivered/5 to-status-delivered/10">
                  <CardContent className="p-4">
                    <div className="flex items-center gap-3">
                      <div className="w-10 h-10 rounded-lg bg-status-delivered/20 flex items-center justify-center">
                        <Wallet className="w-5 h-5 text-status-delivered" />
                      </div>
                      <div>
                        <p className="text-sm text-muted-foreground">Total Net Earnings</p>
                        <p className="text-2xl font-bold text-status-delivered">{earnings.total.toFixed(0)}</p>
                      </div>
                    </div>
                  </CardContent>
                </Card>
                <Card className="border-0 shadow-sm">
                  <CardContent className="p-4">
                    <div className="flex items-center gap-3">
                      <div className="w-10 h-10 rounded-lg bg-secondary flex items-center justify-center">
                        <Package className="w-5 h-5 text-muted-foreground" />
                      </div>
                      <div>
                        <p className="text-sm text-muted-foreground">Orders Delivered</p>
                        <p className="text-2xl font-bold">{earnings.orderCount}</p>
                      </div>
                    </div>
                  </CardContent>
                </Card>
              </div>
            </div>

            {/* Settlement Breakdown */}
            <Card className="border-0 shadow-sm">
              <CardHeader>
                <CardTitle className="text-base flex items-center gap-2">
                  <IndianRupee className="w-4 h-4" />
                  Settlement Breakdown
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-3">
                <div className="flex items-center justify-between p-3 bg-secondary/50 rounded-lg">
                  <span className="text-sm text-muted-foreground">Gross Order Value</span>
                  <span className="font-bold">{earnings.grossTotal.toFixed(0)}</span>
                </div>
                <div className="flex items-center justify-between p-3 bg-destructive/5 rounded-lg border border-destructive/10">
                  <span className="text-sm text-muted-foreground flex items-center gap-2">
                    <Bike className="w-4 h-4" />
                    Delivery Fees (Platform)
                  </span>
                  <span className="font-medium text-destructive">- {earnings.deliveryFees.toFixed(0)}</span>
                </div>
                <div className="flex items-center justify-between p-3 bg-destructive/5 rounded-lg border border-destructive/10">
                  <span className="text-sm text-muted-foreground flex items-center gap-2">
                    <IndianRupee className="w-4 h-4" />
                    Platform Fees (8/order)
                  </span>
                  <span className="font-medium text-destructive">- {earnings.platformFees.toFixed(0)}</span>
                </div>
                <div className="flex items-center justify-between p-3 bg-destructive/5 rounded-lg border border-destructive/10">
                  <span className="text-sm text-muted-foreground flex items-center gap-2">
                    <Percent className="w-4 h-4" />
                    Commission ({restaurant?.commission_rate || 15}%)
                  </span>
                  <span className="font-medium text-destructive">- {earnings.commission.toFixed(0)}</span>
                </div>
                <div className="flex items-center justify-between p-4 bg-accent/10 rounded-lg border border-accent/20">
                  <span className="text-sm font-semibold text-accent">Your Net Earnings</span>
                  <span className="font-bold text-lg text-accent">{earnings.total.toFixed(0)}</span>
                </div>
              </CardContent>
            </Card>

            {/* Commission Info */}
            <Card className="border-0 shadow-sm">
              <CardHeader>
                <CardTitle className="text-base">Payment Schedule</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="flex items-center justify-between p-3 bg-secondary/50 rounded-lg">
                  <span className="text-sm text-muted-foreground">Settlement Frequency</span>
                  <span className="font-medium">Weekly</span>
                </div>
                <p className="text-xs text-muted-foreground mt-3">
                  Earnings are settled every Monday. Delivery fees and platform fees are collected by the platform. Commission is deducted from your order subtotal.
                </p>
              </CardContent>
            </Card>
          </TabsContent>

          {/* Settings Tab */}
          <TabsContent value="settings" className="space-y-6">
            <Card className="border-0 shadow-sm">
              <CardHeader>
                <CardTitle className="text-base flex items-center gap-2">
                  <MapPin className="w-4 h-4" />
                  Shop Location
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <p className="text-sm text-muted-foreground">
                  Set your shop's GPS location so customers can see distance and get accurate delivery estimates.
                </p>

                <div className="flex items-center justify-between p-3 bg-secondary/50 rounded-lg">
                  <div>
                    <p className="text-sm font-medium">Current Location</p>
                    {restaurant?.lat && restaurant?.lng ? (
                      <p className="text-xs text-accent flex items-center gap-1 mt-1">
                        <MapPin className="w-3 h-3" />
                        {Number(restaurant.lat).toFixed(4)}, {Number(restaurant.lng).toFixed(4)}
                      </p>
                    ) : (
                      <p className="text-xs text-muted-foreground mt-1">Not set</p>
                    )}
                  </div>
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={detectCurrentLocation}
                    disabled={detectingLocation}
                    className="gap-1"
                  >
                    {detectingLocation ? (
                      <Loader2 className="w-3 h-3 animate-spin" />
                    ) : (
                      <Navigation className="w-3 h-3" />
                    )}
                    Detect Location
                  </Button>
                </div>

                <div className="grid grid-cols-2 gap-3">
                  <div className="space-y-1">
                    <Label className="text-xs">Latitude</Label>
                    <Input
                      type="number"
                      step="any"
                      value={restaurantForm.lat}
                      onChange={(e) => setRestaurantForm({ ...restaurantForm, lat: e.target.value })}
                      placeholder="12.9716"
                    />
                  </div>
                  <div className="space-y-1">
                    <Label className="text-xs">Longitude</Label>
                    <Input
                      type="number"
                      step="any"
                      value={restaurantForm.lng}
                      onChange={(e) => setRestaurantForm({ ...restaurantForm, lng: e.target.value })}
                      placeholder="77.5946"
                    />
                  </div>
                </div>

                <Button
                  onClick={updateShopLocation}
                  disabled={!restaurantForm.lat || !restaurantForm.lng}
                  className="w-full"
                >
                  Save Location
                </Button>
              </CardContent>
            </Card>

            <Card className="border-0 shadow-sm">
              <CardHeader>
                <CardTitle className="text-base flex items-center gap-2">
                  <Store className="w-4 h-4" />
                  Shop Details
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-3">
                <div className="flex items-center justify-between p-3 bg-secondary/50 rounded-lg">
                  <span className="text-sm text-muted-foreground">Shop Name</span>
                  <span className="font-medium">{restaurant?.name}</span>
                </div>
                <div className="flex items-center justify-between p-3 bg-secondary/50 rounded-lg">
                  <span className="text-sm text-muted-foreground">Address</span>
                  <span className="font-medium text-sm text-right max-w-[200px] truncate">{restaurant?.address}</span>
                </div>
                <div className="flex items-center justify-between p-3 bg-secondary/50 rounded-lg">
                  <span className="text-sm text-muted-foreground">Verification Status</span>
                  <span className={`font-medium ${restaurant?.is_verified ? 'text-accent' : 'text-status-pending'}`}>
                    {restaurant?.is_verified ? 'Verified' : 'Pending'}
                  </span>
                </div>
              </CardContent>
            </Card>
          </TabsContent>
        </Tabs>
      </div>
    </div>
  );
}
